// n8n Code node (JavaScript)

const results = [];

for (const item of items) {
  try {
    const rawOutput = item.json.output;

    // Extract JSON between ```json and ```
    const match = rawOutput.match(/```json([\s\S]*?)```/);

    if (match && match[1]) {
      const parsed = JSON.parse(match[1]);

      results.push({ json: parsed });
    } else {
      // If no JSON block found, push raw output for debugging
      results.push({ json: { error: "No JSON found", raw: rawOutput } });
    }

  } catch (err) {
    results.push({ json: { error: err.message, raw: item.json.output } });
  }
}

return results;
