// 1) Get the raw text from LLM
const raw = $json?.message?.content || "";

// 2) Remove the meta lead-in and split lines
let lines = raw.split("\n")
  .map(l => l.trim())
  .filter(Boolean);

// 3) Keep only likely idea lines (numbered items or bold lines)
//    e.g. "1. **\"Why ...\"**"
const ideaLines = lines.filter(l =>
  /^\d+\.\s*/.test(l) ||                         // numbered
  /^\*\*.+\*\*$/.test(l)                         // fully bold
);

// 4) Clean each line: strip numbering, bold, surrounding quotes, trailing punctuation
function cleanOne(s){
  return s
    .replace(/^\d+\.\s*/, "")                    // remove "1. "
    .replace(/^\*\*|\*\*$/g, "")                 // remove **bold**
    .replace(/^[-–•\s]+/, "")                    // leading bullets/dashes
    .replace(/^"+|"+$/g, "")                     // leading/trailing quotes
    .replace(/[”“]/g, '"')                       // smart quotes normalize
    .replace(/^"|"$/g, "")                       // quotes again (after normalize)
    .replace(/\s{2,}/g, " ")                     // collapse spaces
    .replace(/\.*\s*$/,".")                      // ensure single period end
    .trim();
}

// 5) Extract ideas; also catch cases where the text spans next line (ignore empty)
let ideas = ideaLines.map(cleanOne)
  .filter(i => i.length > 0);

// 6) If we somehow got nothing (format changed), fallback: regex capture quoted chunks or numbered after the colon
if (ideas.length === 0) {
  const numbered = [...raw.matchAll(/^\s*\d+\.\s*(.+)$/gm)].map(m => cleanOne(m[1]));
  const quoted = [...raw.matchAll(/"([^"]{10,})"/g)].map(m => cleanOne(m[1]));
  ideas = (numbered.length ? numbered : quoted).filter(Boolean);
}

// 7) Deduplicate exacts and strip trailing doubled quotes if any
ideas = [...new Set(ideas.map(i => i.replace(/"+$/,"").trim()))];

// 8) Return as a single item
return [{ json: { ideas } }];

const past = items[0].json.pastIdeas.map(p => p.trim().toLowerCase());
const ideas = items[0].json.ideas;


const kept_exact = [];
const rejected_exact = [];

for (const idea of ideas) {
  if (past.includes(idea.trim().toLowerCase())) {
    rejected_exact.push(idea);
  } else {
    kept_exact.push(idea);
  }
}

return [{
  json: {
    kept_exact,
    rejected_exact,
    counts: {
      kept: kept_exact.length,
      rejected: rejected_exact.length,
      past
    }
  }
}];

const pastIdeas = items.map(i => i.json.idea).filter(Boolean);

return [{
  json: {
    pastIdeas
  }
}];

