try {
  const hook = ($json.hook || "").trim();
  let body = ($json.body || "").trim();
  const notes = Array.isArray($json.notes) ? $json.notes : [];
  const brief = $json.brief || {};
  let cta = ($json.cta || "Comments").trim();
  let hashtags = Array.isArray($json.hashtags) ? $json.hashtags.slice(0, 32) : [];
  let first_comment = ($json.first_comment || "").trim();

  // -------------------------
  // 1) Normalize spacing & punctuation
  // -------------------------
  // Collapse 3+ newlines to 2
  body = body.replace(/\r/g, "")
             .replace(/\n{3,}/g, "\n\n")
             .replace(/[ \t]+\n/g, "\n");

  // Fix spaced hyphenated words: "AI- native" -> "AI-native"
  // Avoid bullet lines that begin with "- "
  body = body.split("\n").map(line => {
    if (/^\s*-\s/.test(line)) return line; // keep bullets as-is for now
    return line.replace(/\b([A-Za-z0-9]+)\s*-\s*([A-Za-z0-9]+)\b/g, "$1-$2");
  }).join("\n");

  // -------------------------
  // 2) Bullet hygiene & scannability
  // -------------------------
  // Ensure "- " (hyphen + space) bullets
  body = body.replace(/^\s*-\s*/gm, "- ");
  // Add a blank line before and after bullet blocks
  body = body.replace(/([^\n])\n(- .+(?:\n- .+)+)/g, "$1\n\n$2")
             .replace(/((?:- .+(?:\n|$))+)(\S)/g, "$1\n$2");

  // -------------------------
  // 3) Ensure a question if CTA = Comments
  // -------------------------
  const endsWithQuestion = /\?\s*$/.test(body.trim());
  if (cta === "Comments" && !endsWithQuestion) {
    // If last 2 lines already contain a question, skip
    const tail = body.split("\n").slice(-3).join(" ");
    if (!/\?/.test(tail)) {
      body = body.trim() + "\n\nWhat do you think?";
    }
  }

  // -------------------------
  // 4) Hashtag hygiene (dedupe, cap 8, casing)
  // -------------------------
  // Preferred casing map for common tags
  const preferred = {
    "ai": "AI",
    "upi": "UPI",
    "productmanagement": "ProductManagement",
    "productstrategy": "ProductStrategy",
    "a iagents": "AIAgents", // guard against odd spacing
    "aiagents": "AIAgents",
    "indiatch": "IndiaTech",
    "indiatech": "IndiaTech",
    "distribution": "Distribution",
    "growth": "Growth",
    "fintech": "Fintech",
    "digitalpayments": "DigitalPayments",
    "developerexperience": "DeveloperExperience",
    "platformstrategy": "PlatformStrategy",
    "startups": "Startups",
    "innovation": "Innovation",
    "workflows": "Workflows",
  };

  const toPascal = (s) => s
    .split(/[^A-Za-z0-9]+/).filter(Boolean)
    .map(w => w.charAt(0).toUpperCase() + w.slice(1).toLowerCase())
    .join("");

  hashtags = hashtags
    .map(h => String(h).trim())
    .filter(Boolean)
    .map(h => h.replace(/^#/, ""))                 // strip leading #
    .map(h => h.replace(/\s+/g, ""))               // no spaces
    .map(h => preferred[h.toLowerCase()] || toPascal(h)) // apply preferred casing or PascalCase
    .map(h => "#" + h)
    .filter((h, i, arr) => arr.indexOf(h) === i)   // dedupe
    .slice(0, 8);                                   // cap 8

  // -------------------------
  // 5) First comment fallback (optional)
  // -------------------------
  if (!first_comment) {
    first_comment = (cta === "Comments")
      ? "What’s one rail that would make your AI unavoidable this quarter?"
      : "Follow for more product + AI breakdowns.";
  }

  // -------------------------
  // 6) Recompute char_count (visible: hook + body)
  // -------------------------
  const char_count = ((hook ? hook + "\n\n" : "") + body).length;

