// Cleans duplicates of the hook from body, enforces LinkedIn limits, and returns a compliant pack.

function smartTrim(text, limit) {
  if (text.length <= limit) return text;
  const cut = text.slice(0, limit);
  // Try to end at a sentence or line boundary
  const lastStop = Math.max(
    cut.lastIndexOf(". "),
    cut.lastIndexOf("! "),
    cut.lastIndexOf("? "),
    cut.lastIndexOf("\n")
  );
  if (lastStop > 40) return cut.slice(0, lastStop + 1).trim(); // keep a sensible boundary
  return cut.trimEnd() + "…";
}

return items.map(item => {
  const data = item.json || {};
  const MAX_CHARS = 3000; // LinkedIn hard limit
  const hook = (data.hook || "").trim();
  let body = (data.body || "").replace(/\r/g, "");
  let hashtags = Array.isArray(data.hashtags) ? data.hashtags : [];
  const cta = (data.cta || "").trim();
  const first_comment = (data.first_comment || "").trim();
  const brief = data.brief || {};
  const notes = Array.isArray(data.notes) ? data.notes : [];

  // 1) Remove duplicate hook lines from the start of body
  const hookNorm = hook.trim();
  let lines = body.split("\n");
  while (lines.length && lines[0].trim() === hookNorm) lines.shift(); // remove repeated hook(s)
  while (lines.length && lines[0].trim() === "") lines.shift();       // remove leading blank lines
  body = lines.join("\n").trim();

  // 2) Build visible text = hook + blank line + body
  //    (always show hook at top once)
  const visiblePrefix = hook ? hook + "\n\n" : "";
  // enforce max by trimming ONLY the body portion
  const allowedBodyLimit = Math.max(0, MAX_CHARS - visiblePrefix.length);
  const bodyTrimmed = smartTrim(body, allowedBodyLimit);

  const visible = visiblePrefix + bodyTrimmed;

  // 3) Hashtag cap (10), normalize # and dedupe
  hashtags = [...new Set(
    hashtags
      .map(h => "#" + String(h || "").replace(/^#/, "").replace(/\s+/g, ""))
  )].slice(0, 10);

  // 4) Final char count of visible text
  const char_count = visible.length;

  return {
    json: {
      hook,                 // unchanged
      body: bodyTrimmed,    // cleaned & possibly trimmed
      notes,
      cta,
      hashtags,
      first_comment,
      char_count,
      brief
    }
  };
});

// Expecting merged item with text JSON + binary.image
const j = $json || {};
const b = $binary?.image || $binary?.data || null;

if (!j.hook && !j.body) {
  throw new Error('No post fields found. Ensure Merge node combines 16_FormatCompliance (JSON) with 17_GenerateImage (binary).');
}
if (!b) {
  throw new Error('No image binary found. In Image node set Binary Property = "image" (or "data") and wire it into the Merge.');
}

// Normalize
const hashtags = Array.isArray(j.hashtags) ? j.hashtags : (j.hashtags ? String(j.hashtags).split(/\s+/) : []);
const finalPost = `
${j.hook || ''}

${j.body || ''}

${hashtags.length ? hashtags.join(' ') : ''}${j.cta ? `\n\n${j.cta}` : ''}
`.trim();

return [{
  json: {
    ...j,
    final_post: finalPost,
    image_provider: 'image_gen_node',
    image_filename: b.fileName || 'post.png',
    image_mime: b.mimeType || 'image/png',
    image_size: b.fileSize || '',
    image_binary_id: b.id || ''
  },
  binary: { image: b }
}];

// 18b_BuildSheetRow

const rows = [];

for (const item of items) {
  const x = item.json;

  // === IDEA (dedupe safety) ===
  const pickSentence = (txt) => {
    if (!txt) return '';
    const s = txt.split(/[.?!]/)[0];
    return s.length > 100 ? s.slice(0, 100) : s;
  };

  const ideaTextRaw =
    x.locked_idea ||
    x.chosenIdea ||
    x.idea ||
    pickSentence(x.hook) ||
    pickSentence(x.body) ||
    pickSentence(x.final_post);

  const idea = (ideaTextRaw || '').trim();
  const ideaSlug = idea.toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-|-$/g, '');

  // === Hash for exact dedupe ===
  const crypto = require('crypto');
  const ideaHash = crypto.createHash('md5').update(idea).digest('hex');

  // === Timestamps ===
  const tsISO = new Date().toISOString();
  const tsIST = new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' });

  // === Sheet Row Object ===
  rows.push({
    timestamp_iso: tsISO,
    timestamp_ist: tsIST,

    post_id: x.id || '',

    idea,
    idea_slug: ideaSlug,
    idea_hash: ideaHash,

    hook: x.hook || '',
    body: x.body || '',
    cta: x.cta || '',
    hashtags: Array.isArray(x.hashtags) ? x.hashtags.join(' ') : (x.hashtags || ''),
    first_comment: x.first_comment || '',
    final_post: x.final_post || '',

    notes: Array.isArray(x.notes) ? x.notes.join(' | ') : (x.notes || ''),

    image_url: x.webViewLink || x.webContentLink || '',
    image_filename: x.image_filename || x.name || '',
    image_mime: x.image_mime || x.mimeType || '',
    image_size: x.image_size || x.fileSize || '',

    // Extra debug/meta
    image_binary_id: x.image_binary_id || '',
    provider: x.image_provider || '',
  });
}

return rows.map(r => ({ json: r }));
