// Assume input is in items[0].json$input.first().json.grade
const feedback = $input.first().json;

function formatSuggestions(suggestions) {
  return suggestions.map((s, index) => {
    return `### ${index + 1}. ${s.category.toUpperCase()}
- **Issue**: ${s.whatIsTheIssue}
- **Why it Matters**: ${s.whyItMatters}
- **How to Fix**: ${s.howToFix}
- **Priority**: ${s.priority.toUpperCase()}\n`;
  }).join('\n');
}

function formatStrengths(strengths) {
  return strengths.map(s => `- ${s}`).join('\n');
}

function formatScores(scores) {
  return Object.entries(scores)
    .map(([k, v]) => `- **${k}**: ${v}`)
    .join('\n');
}

const docText = `
# Conversion Audit Report

**Grade:** ${feedback.grade}  
**Overall Score:** ${feedback.score}/100

---

## Suggestions for Improvement

${formatSuggestions(feedback.suggestions)}

---

## Strengths

${formatStrengths(feedback.strengths)}

---

## Conversion Score Breakdown

${formatScores(feedback.conversionScore)}
`;

return [
  {
    json: {
      docText
    }
  }
];
