// Get the current date and time
const now = new Date();

// Combine the output from all RSS feed nodes into one array
// Input structure: [ { "data": [ { RSS feed item } ] } ]
const allArticles = items.flatMap(item => item.json.data);

// Filter out articles older than 24 hours
const filteredArticles = allArticles.filter(item => {
    const pubDate = new Date(item.pubDate);
    const diff = (now - pubDate) / (1000 * 60 * 60);
    return diff <= 24;
});

// Extract key fields including publish date
const articlesText = filteredArticles.map(item => {
    return `\nTitle: ${item.title}\nLink: ${item.link}\nPublished: ${item.pubDate}\nText snippet: ${item.contentSnippet}\n---\n`;
}).join('');

// Return the filtered and formatted articles
return [{ json: { articlesText } }];
