const chunkSize = 4000; // characters per chunk

const text = $input.first().json.text ?? "";
const name = $("Webhook").first().json.body.name;
const email = $("Webhook").first().json.body.email;
const title = $input.first().json.info.Title;

const chunks = [];

for (let i = 0; i < text.length; i += chunkSize) {
  chunks.push({
    chunk: text.slice(i, i + chunkSize),
    chunkIndex: i / chunkSize + 1,
    name,
    email,
    title,
  });
}

return chunks.map((c) => ({ json: c }));

