const data = $input.first().json.data.apiData.results;

// Basic metadata
const basic = data.basic;
const adv = data.advanced;
const perf = data.performance;
const sec = data.security;

const formatStatus = (status) => status === 'passed' ? '‚úÖ Passed' : '‚ùå Failed';

// Format keyword counts
const keywordStats = Object.entries(basic.keywords || {})
  .map(([word, count]) => `- **${word}**: ${count}`)
  .join('\n');

const keywordsInTD = basic.keywordsInTitleDescription?.value || {};
const titleKeywords = Object.entries(keywordsInTD.title || {}).map(([k, v]) => `${k} (${v})`).join(', ');
const descKeywords = Object.entries(keywordsInTD.description || {}).map(([k, v]) => `${k} (${v})`).join(', ');

// Format no alt images
const missingAltImgs = basic.noImgAltAtts?.value?.length || 0;

// Format H1/H2 Tags
const h1Tags = basic.h1Tags?.value || [];
const h2Tags = basic.h2Tags?.value || [];

const formatted = `
# üîç SEO Site Audit Summary

## üìù Page Metadata
- **Title:** ${basic.title?.value || 'N/A'} (${basic.title?.length || 0} chars) ‚Äî ${formatStatus(basic.title?.status)}
- **Description:** ${basic.description?.value || 'N/A'} (${basic.description?.length || 0} chars) ‚Äî ${formatStatus(basic.description?.status)}
- **Title Keywords:** ${titleKeywords || 'None'}
- **Description Keywords:** ${descKeywords || 'None'}

## üè∑Ô∏è Keyword Density
${keywordStats || 'No keywords found.'}

## üß± Headers
- **H1 Tags:** ${h1Tags.length > 0 ? h1Tags.join('; ') : '‚ùå None Found'}
- **H2 Tags:** ${h2Tags.length > 0 ? h2Tags.join('; ') : '‚ùå None Found'}

## üñºÔ∏è Image Optimization
- Missing \`alt\` tags: ${missingAltImgs} ${missingAltImgs > 0 ? '‚ùå Needs improvement' : '‚úÖ All good'}

## üîó Links Summary
- Internal Links: ${basic.linksRatio?.value?.internal || 0}
- External Links: ${basic.linksRatio?.value?.external || 0} ‚Äî ${formatStatus(basic.linksRatio?.status)}

## üì± Search Preview
${adv.searchPreview || 'N/A'}

## üì¶ Performance
- JS Unminified: ${perf.unminifiedJs?.value?.length || 0} ‚Äî ${formatStatus(perf.unminifiedJs?.status)}
- CSS Unminified: ${perf.unminifiedCss?.value?.length || 0} ‚Äî ${formatStatus(perf.unminifiedCss?.status)}
- Total Page Objects: ${perf.pageObjects?.value?.total || 0} ‚Äî ${formatStatus(perf.pageObjects?.status)}
- Page Size: ${perf.pageSize?.value || 0} KB
- Response Time: ${perf.responseTime?.value || 0} sec

## üõ°Ô∏è Security
- Secure Connection: ${formatStatus(sec.secureConnection?.status)}
- Google Safe Browsing: ${formatStatus(sec.googleSafeBrowsing?.status)}
- Directory Listing: ${formatStatus(sec.directoryListing?.status)}

## üß† Structured Data
- Open Graph Tags: ${adv.openGraph?.status === 'error' ? `‚ùå Missing: ${adv.openGraph?.value?.join(', ')}` : '‚úÖ Present'}
- Schema.org Markup: ${formatStatus(adv.schema?.status)}

---
`;

return [
  {
    json: {
      docContent: formatted.trim()
    }
  }
];
