// Capture pipeline start time at the very beginning
const now = new Date();
return [{
  json: {
    pipeline_start_time: now.toISOString(),
    pipeline_start_timestamp: now.getTime(),
    pipeline_start_message: `Pipeline started at ${now.toLocaleString()}`
  }
}];

// Process file list with configuration and pipeline start time
const configData = $input.first().json || {};
const searchData = $input.last().json || {};

const output = searchData.stdout || '';
const stderr = searchData.stderr || '';

// Preserve pipeline start time
const pipeline_start_time = configData.pipeline_start_time || '';
const pipeline_start_timestamp = configData.pipeline_start_timestamp || Date.now();

console.log('=== Processing File List ===');
console.log('Pipeline start time:', pipeline_start_time);
console.log('Config data:', configData);
console.log('Raw output:', output);
console.log('Files found:', output ? output.split(/\r?\n/).filter(x => x.trim()).length : 0);

const config = {
  converter_path: configData.converter_path || '',
  source_folder: configData.source_folder || '',
  output_folder: configData.output_folder || '',
  include_subfolders: configData.include_subfolders || false,
  file_extension: configData.file_extension || '.rvt',
  options: configData.options || ''
};

if (!output || !output.trim()) {
  return [{
    json: {
      files: [],
      total_files: 0,
      message: `No files found with extension '${config.file_extension}' in ${config.source_folder}`,
      error: stderr || 'No output from search command',
      config: config,
      pipeline_start_time: pipeline_start_time,
      pipeline_start_timestamp: pipeline_start_timestamp
    }
  }];
}

let normalizedExtension = (config.file_extension || '').trim();
if (normalizedExtension && !normalizedExtension.startsWith('.')) {
  normalizedExtension = '.' + normalizedExtension;
}

let rawPaths = [];
try {
  rawPaths = output.trim().split(/\r?\n/)
    .map(path => (path || '').trim())
    .filter(path => path && path.length > 0);
} catch (error) {
  console.log('Error splitting output:', error);
  return [{
    json: {
      files: [],
      total_files: 0,
      message: `Error processing search results: ${error.message}`,
      config: config,
      pipeline_start_time: pipeline_start_time,
      pipeline_start_timestamp: pipeline_start_timestamp
    }
  }];
}

const filePaths = rawPaths.filter(path => {
  if (!path || !normalizedExtension) return false;
  const lowerPath = path.toLowerCase();
  const lowerExt = normalizedExtension.toLowerCase();
  return lowerPath.endsWith(lowerExt);
});

if (filePaths.length === 0) {
  return [{
    json: {
      files: [],
      total_files: 0,
      message: `No files found with extension '${normalizedExtension}' in ${config.source_folder}`,
      config: config,
      pipeline_start_time: pipeline_start_time,
      pipeline_start_timestamp: pipeline_start_timestamp
    }
  }];
}

const files = filePaths.map((filePath, index) => {
  let fileName = 'unknown_file';
  let fileNameWithoutExt = 'unknown_file';

  try {
    if (filePath && typeof filePath === 'string') {
      const normalizedPath = filePath.replace(/\\/g, '/');
      const pathParts = normalizedPath.split('/');
      fileName = pathParts[pathParts.length - 1] || 'unknown_file';

      console.log(`Processing file ${index + 1}: ${fileName} from path: ${filePath}`);

      const lastDotIndex = fileName.lastIndexOf('.');
      if (lastDotIndex > 0) {
        fileNameWithoutExt = fileName.substring(0, lastDotIndex);
      } else {
        fileNameWithoutExt = fileName;
      }
    }
  } catch (error) {
    console.log(`Error parsing file path ${filePath}:`, error);
  }

  const extWithoutDot = normalizedExtension ? normalizedExtension.substring(1) : 'unknown';

  return {
    index: index + 1,
    file_path: filePath,
    file_name: fileName,
    file_name_without_ext: fileNameWithoutExt,
    converter_path: config.converter_path,
    expected_output: `${config.output_folder}\\${fileNameWithoutExt}_${extWithoutDot}.xlsx`,
    expected_output_dae: `${config.output_folder}\\${fileNameWithoutExt}_${extWithoutDot}.dae`,
    config: config,
    options: config.options,
    pipeline_start_time: pipeline_start_time,
    pipeline_start_timestamp: pipeline_start_timestamp
  };
});

console.log('Processed files:', files.map(f => f.file_name));

return [{
  json: {
    files: files,
    total_files: files.length,
    message: `Found ${files.length} files to convert`,
    config: config,
    pipeline_start_time: pipeline_start_time,
    pipeline_start_timestamp: pipeline_start_timestamp
  }
}];

// Capture conversion start time and preserve ALL data including pipeline start
return $input.all().map(item => {
  const json = {...item.json};
  json.conversion_start_time = new Date().toISOString();
  json.conversion_start_timestamp = Date.now();
  console.log(`Starting conversion for: ${json.file_name} at ${json.conversion_start_time}`);
  console.log(`Pipeline started at: ${json.pipeline_start_time}`);
  return {json};
});

// Calculate actual conversion time and rename outputs - preserve pipeline start time
return $input.all().map(item => {
  const json = {...item.json};

  // Rename stdout/stderr
  json.conversion_stdout = json.stdout || '';
  json.conversion_stderr = json.stderr || '';
  json.conversion_exitCode = json.exitCode || 0;
  delete json.stdout;
  delete json.stderr;
  delete json.exitCode;

  // Calculate actual processing time with decimal precision
  if (json.conversion_start_timestamp) {
    const endTime = Date.now();
    const startTime = json.conversion_start_timestamp;
    const processingTimeMs = endTime - startTime;
    json.processing_time = processingTimeMs / 1000;
    json.processing_time_ms = processingTimeMs;
    json.conversion_end_time = new Date().toISOString();
    json.conversion_end_timestamp = endTime;
    console.log(`Conversion completed for: ${json.file_name} in ${json.processing_time.toFixed(2)} seconds`);
  } else {
    json.processing_time = 0;
    json.processing_time_ms = 0;
    console.warn(`No start timestamp found for: ${json.file_name}`);
  }

  // Ensure pipeline start time is preserved
  console.log(`Pipeline start preserved: ${json.pipeline_start_timestamp}`);

  return {json};
});

// Complete file verification with processing time - preserve pipeline start time
const allInputs = $input.all();
const mergedDataItems = $('Merge Data Before Verification1').all();

console.log(`=== Complete File Verification ===`);
console.log(`Total verification inputs: ${allInputs.length}`);
console.log(`Total merged data items: ${mergedDataItems.length}`);

const results = [];

// Process each file
allInputs.forEach((input, index) => {
  const verificationData = input.json || {};

  // Find corresponding merged data by matching index
  const mergedData = mergedDataItems[index]?.json || {};

  // Extract all necessary data from merged node
  const fileName = mergedData.file_name || 'unknown_file';
  const filePath = mergedData.file_path || '';
  const expectedOutput = mergedData.expected_output || '';
  const expectedOutputDae = mergedData.expected_output_dae || '';
  const fileIndex = mergedData.index || index + 1;
  const processingTime = mergedData.processing_time || 0;
  const config = mergedData.config || {};
  const conversionStartTime = mergedData.conversion_start_time || '';
  const conversionEndTime = mergedData.conversion_end_time || '';
  const conversionEndTimestamp = mergedData.conversion_end_timestamp || Date.now();

  // IMPORTANT: Preserve pipeline start time
  const pipelineStartTime = mergedData.pipeline_start_time || '';
  const pipelineStartTimestamp = mergedData.pipeline_start_timestamp || 0;

  console.log(`Processing verification for file ${fileIndex}: ${fileName}`);
  console.log('Actual processing time:', processingTime, 'seconds');
  console.log('Pipeline start timestamp:', pipelineStartTimestamp);

  // Get verification output from the current input
  let verificationOutput = verificationData.stdout || verificationData.verification_output || '';

  // Parse PowerShell output
  let successXlsx = false;
  let successDae = false;
  let successRevit = false;
  let fileSizeXlsx = 0;
  let fileSizeDae = 0;
  let fileSizeRevit = 0;

  try {
    if (verificationOutput && verificationOutput.includes('|')) {
      const parts = verificationOutput.split('|');

      parts.forEach(part => {
        if (part && part.includes('XLSX_EXISTS:')) {
          successXlsx = part.includes('XLSX_EXISTS:True');
        }
        if (part && part.includes('XLSX_SIZE:')) {
          const sizeStr = part.replace('XLSX_SIZE:', '').trim();
          fileSizeXlsx = parseInt(sizeStr) || 0;
        }
        if (part && part.includes('DAE_EXISTS:')) {
          successDae = part.includes('DAE_EXISTS:True');
        }
        if (part && part.includes('DAE_SIZE:')) {
          const sizeStr = part.replace('DAE_SIZE:', '').trim();
          fileSizeDae = parseInt(sizeStr) || 0;
        }
        if (part && part.includes('REVIT_EXISTS:')) {
          successRevit = part.includes('REVIT_EXISTS:True');
        }
        if (part && part.includes('REVIT_SIZE:')) {
          const sizeStr = part.replace('REVIT_SIZE:', '').trim();
          fileSizeRevit = parseInt(sizeStr) || 0;
        }
      });
    }
  } catch (error) {
    console.log('Error parsing verification output:', error);
  }

  const finalSuccess = successXlsx && successDae && fileSizeXlsx > 0 && fileSizeDae > 0;

  const statusMessage = finalSuccess 
    ? `‚úÖ [${fileIndex}] Successfully converted: ${fileName} (XLSX: ${Math.round(fileSizeXlsx/1024)}KB, DAE: ${Math.round(fileSizeDae/1024)}KB, ${processingTime.toFixed(1)}s)`
    : `‚ùå [${fileIndex}] Failed to convert: ${fileName} (XLSX: ${successXlsx && fileSizeXlsx > 0 ? '‚úì' : '‚úó'}, DAE: ${successDae && fileSizeDae > 0 ? '‚úì' : '‚úó'}, ${processingTime.toFixed(1)}s)`;

  const result = {
    file_name: fileName,
    file_path: filePath,
    expected_output: expectedOutput,
    expected_output_dae: expectedOutputDae,
    success: finalSuccess,
    success_xlsx: successXlsx && fileSizeXlsx > 0,
    success_dae: successDae && fileSizeDae > 0,
    processing_time: processingTime,
    file_size: fileSizeXlsx + fileSizeDae,
    file_size_xlsx: fileSizeXlsx,
    file_size_dae: fileSizeDae,
    file_size_revit: fileSizeRevit,
    index: fileIndex,
    status: finalSuccess ? 'converted' : 'failed',
    message: statusMessage,
    timestamp: new Date().toISOString(),
    verification_output: verificationOutput,
    config: config,
    conversion_start_time: conversionStartTime,
    conversion_end_time: conversionEndTime,
    conversion_end_timestamp: conversionEndTimestamp,
    pipeline_start_time: pipelineStartTime,
    pipeline_start_timestamp: pipelineStartTimestamp
  };

  console.log(`Verification complete for ${fileName}: ${result.message}`);
  results.push({ json: result });
});

console.log(`=== Returning ${results.length} verification results ===`);
return results;

