// Find the pipeline start timestamp from the data
let pipelineStartTimestamp = null;
let config = {};

// Extract timing and config from results
for (const result of results) {
  if (result.json) {
    // Get pipeline start timestamp
    if (result.json.pipeline_start_timestamp && !pipelineStartTimestamp) {
      pipelineStartTimestamp = result.json.pipeline_start_timestamp;
      console.log('Found pipeline start timestamp:', pipelineStartTimestamp);
    }

    // Get config
    if (result.json.config && Object.keys(result.json.config).length > 0) {
      config = result.json.config;
    }
  }
}

// Check if no-collada option is enabled
const noColladaEnabled = config.options && config.options.includes('no-collada');
console.log('No-collada mode enabled:', noColladaEnabled);

// Calculate total pipeline time in minutes
let totalPipelineMinutes = '0.00';
if (pipelineStartTimestamp) {
  const totalMs = pipelineEndTime - pipelineStartTimestamp;
  totalPipelineMinutes = (totalMs / 60000).toFixed(2);
  console.log('Pipeline timing:', {
    start: pipelineStartTimestamp,
    end: pipelineEndTime,
    totalMs: totalMs,
    totalMinutes: totalPipelineMinutes
  });
} else {
  console.warn('No pipeline start timestamp found!');
}

// Use config output folder or fallback
const outputFolder = config.output_folder || 'C:\\temp';

// Count successful and failed conversions
let totalFiles = 0;
let successfulConversions = 0;
let failedConversions = 0;
let totalInputSize = 0;
let totalOutputSize = 0;

const successfulFiles = [];
const failedFiles = [];

results.forEach((result, index) => {
  const data = result.json || {};
  totalFiles++;

  const processingTime = data.processing_time || 0;
  const xlsxSuccess = data.success_xlsx || false;
  const daeSuccess = data.success_dae || false;
  const fileSizeRevit = data.file_size_revit || 0;
  const fileSizeXlsx = data.file_size_xlsx || 0;
  const fileSizeDae = data.file_size_dae || 0;

  totalInputSize += fileSizeRevit;

  // Modified success logic for no-collada mode
  let isSuccess = false;
  if (noColladaEnabled) {
    // In no-collada mode, only XLSX matters
    isSuccess = xlsxSuccess;
    if (isSuccess) {
      totalOutputSize += fileSizeXlsx;
    }
  } else {
    // Normal mode: both XLSX and DAE must succeed
    isSuccess = xlsxSuccess && daeSuccess;
    if (isSuccess) {
      totalOutputSize += fileSizeXlsx + fileSizeDae;
    }
  }

  if (isSuccess) {
    successfulConversions++;
    successfulFiles.push({
      name: data.file_name || `File ${index + 1}`,
      originalSize: Math.round(fileSizeRevit / 1024),
      xlsxSize: Math.round(fileSizeXlsx / 1024),
      daeSize: Math.round(fileSizeDae / 1024),
      totalSize: Math.round((fileSizeXlsx + (noColladaEnabled ? 0 : fileSizeDae)) / 1024),
      xlsxPath: data.expected_output || '',
      daePath: data.expected_output_dae || '',
      noCollada: noColladaEnabled
    });
  } else {
    failedConversions++;
    failedFiles.push({
      name: data.file_name || `File ${index + 1}`,
      originalSize: Math.round(fileSizeRevit / 1024),
      xlsxStatus: xlsxSuccess ? '‚úì' : '‚úó',
      daeStatus: daeSuccess ? '‚úì' : '‚úó'
    });
  }
});

const successRate = totalFiles > 0 ? Math.round((successfulConversions / totalFiles) * 100) : 0;

// Generate timestamp for filename
