const timestamp = new Date().toISOString().slice(0,10);
const excelFileName = `CO2_Analysis_Professional_Report_${timestamp}.xlsx`;
const fullPath = `${path}\\${excelFileName}`;

console.log('Excel file will be saved to:', fullPath);

return [{
  json: {
    excel_filename: excelFileName,
    full_path: fullPath,
    directory: path,
    project_file: projectFile
  },
  binary: $input.first().binary
}];
