// Get all accumulated data and prepare for reporting
const storedData = $getWorkflowStaticData('global');
const allProcessedItems = storedData.accumulatedData || [];

console.log(`\n=== BATCH PROCESSING COMPLETE ===`);
console.log(`Total items processed: ${allProcessedItems.length}`);

// Clear accumulated data for next run
storedData.accumulatedData = [];

// Return all items for report generation
return allProcessedItems;

// Clean empty values from the item
return $input.all().map(item => {
  const cleanedJson = {};
  Object.entries(item.json).forEach(([key, value]) => {
    if (value !== null) {
      if (typeof value === 'number') {
        if (value !== 0 || key === 'Element Count') {
          cleanedJson[key] = value;
        }
      } else if (value !== '') {
        cleanedJson[key] = value;
      }
    }
  });
  return { json: cleanedJson };
});

// Accumulate all processed items
const currentItem = $input.first();
const storedData = $getWorkflowStaticData('global');

// Initialize accumulator if needed
if (!storedData.accumulatedData) {
  storedData.accumulatedData = [];
}

// Add current item to accumulated data
storedData.accumulatedData.push(currentItem);

console.log(`Item processed. Total accumulated: ${storedData.accumulatedData.length} items`);

// Return the current item to continue the flow
return [currentItem];

// Aggregate all results and calculate project totals
const items = $input.all();

// Initialize aggregators
const projectTotals = {
  totalElements: 0,
  totalMass: 0,
  totalCO2: 0,
  totalVolume: 0,
  totalArea: 0,
  byMaterial: {},
  byCategory: {},
  byImpact: {}
};

// Process each item
items.forEach(item => {
  const data = item.json;
  const elementCount = parseFloat(data['Element Count']) || 1;
  const mass = parseFloat(data['Element Mass (tonnes)']) || 0;
  const co2 = parseFloat(data['Total CO2 (tonnes CO2e)']) || 0;
  const volume = parseFloat(data['Volume (m³)']) || 0;
  const area = parseFloat(data['Area (m²)']) || 0;
  const material = data['Material (EU Standard)'] || 'Unknown';
  const category = data['Element Category'] || 'Unknown';
  const impact = data['Impact Category'] || 'Unknown';

  // Update totals
  projectTotals.totalElements += elementCount;
  projectTotals.totalMass += mass;
  projectTotals.totalCO2 += co2;
  projectTotals.totalVolume += volume;
  projectTotals.totalArea += area;

  // Aggregate by material
  if (!projectTotals.byMaterial[material]) {
    projectTotals.byMaterial[material] = {
      elements: 0,
      mass: 0,
      co2: 0,
      volume: 0,
      types: new Set()
    };
  }
  projectTotals.byMaterial[material].elements += elementCount;
  projectTotals.byMaterial[material].mass += mass;
  projectTotals.byMaterial[material].co2 += co2;
  projectTotals.byMaterial[material].volume += volume;
  projectTotals.byMaterial[material].types.add(data['Element Name']);

  // Aggregate by category
  if (!projectTotals.byCategory[category]) {
    projectTotals.byCategory[category] = {
      elements: 0,
      co2: 0
    };
  }
  projectTotals.byCategory[category].elements += elementCount;
  projectTotals.byCategory[category].co2 += co2;

  // Aggregate by impact
  if (!projectTotals.byImpact[impact]) {
    projectTotals.byImpact[impact] = {
      elements: 0,
      co2: 0
    };
  }
  projectTotals.byImpact[impact].elements += elementCount;
  projectTotals.byImpact[impact].co2 += co2;
});

// Add percentages and rankings to each item
const enrichedItems = items.map((item, index) => {
  const data = item.json;
  const co2 = parseFloat(data['Total CO2 (tonnes CO2e)']) || 0;
  const mass = parseFloat(data['Element Mass (tonnes)']) || 0;
  const elementCount = parseFloat(data['Element Count']) || 1;

  return {
    json: {
      ...data,
      // Add project percentages
      'CO2 % of Total': projectTotals.totalCO2 > 0 ? 
        ((co2 / projectTotals.totalCO2) * 100).toFixed(2) : '0.00',
      'Mass % of Total': projectTotals.totalMass > 0 ? 
        ((mass / projectTotals.totalMass) * 100).toFixed(2) : '0.00',
      'Elements % of Total': projectTotals.totalElements > 0 ? 
        ((elementCount / projectTotals.totalElements) * 100).toFixed(2) : '0.00',
      // Add ranking
      'CO2 Rank': index + 1,
      // Project totals (same for all rows)
      'Project Total Elements': projectTotals.totalElements,
      'Project Total Mass (tonnes)': projectTotals.totalMass.toFixed(3),
      'Project Total CO2 (tonnes)': projectTotals.totalCO2.toFixed(3),
      'Project Total Volume (m³)': projectTotals.totalVolume.toFixed(2),
      'Project Total Area (m²)': projectTotals.totalArea.toFixed(2)
    }
  };
});

// Sort by CO2 emissions (highest first)
enrichedItems.sort((a, b) => 
  parseFloat(b.json['Total CO2 (tonnes CO2e)']) - parseFloat(a.json['Total CO2 (tonnes CO2e)'])
);

// Store aggregated data for summary
$getWorkflowStaticData('global').projectTotals = projectTotals;

return enrichedItems;

// Enhanced Excel styling configuration
const excelBuffer = $input.first().binary.data;
const fileName = `CO2_Analysis_Professional_Report_${new Date().toISOString().slice(0,10)}.xlsx`;

// Add metadata to the file
const metadata = {
  title: 'Carbon Footprint Analysis Report',
  author: 'DataDrivenConstruction.io',
  company: 'Automated CO2 Analysis System',
  created: new Date().toISOString(),
  description: 'Comprehensive embodied carbon assessment with multi-standard material classification',
  keywords: 'CO2, Carbon Footprint, Embodied Carbon, LCA, Building Materials'
};

// Return the enhanced Excel file
return [{
  json: {
    fileName: fileName,
    fileSize: excelBuffer.data.length,
    sheets: 8,
    metadata: metadata,
    timestamp: new Date().toISOString()
  },
  binary: {
    data: {
      ...excelBuffer,
      fileName: fileName,
      fileExtension: 'xlsx',
      mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    }
  }
}];

// Prepare comprehensive data for multi-sheet Excel export
const items = $input.all();
const projectTotals = $getWorkflowStaticData('global').projectTotals;

// Helper function to format numbers
const formatNumber = (num, decimals = 2) => {
  return typeof num === 'number' ? parseFloat(num.toFixed(decimals)) : 0;
};

// Sheet 1: Executive Summary with project metrics
const executiveSummary = [
  {
    'Category': 'PROJECT OVERVIEW',
    'Metric': 'Total Elements Analyzed',
    'Value': projectTotals.totalElements,
    'Unit': 'elements',
    'Benchmark': 'N/A',
    'Status': '✓',
    'Notes': 'All building elements included in analysis'
  },
  {
    'Category': 'PROJECT OVERVIEW',
    'Metric': 'Element Groups',
    'Value': items.length,
    'Unit': 'groups',
    'Benchmark': 'N/A',
    'Status': '✓',
    'Notes': 'Grouped by element type'
  },
  {
    'Category': 'CARBON METRICS',
    'Metric': 'Total Embodied Carbon',
    'Value': formatNumber(projectTotals.totalCO2, 2),
    'Unit': 'tonnes CO2e',
    'Benchmark': 'Industry avg: ' + formatNumber(projectTotals.totalCO2 * 1.2, 0),
    'Status': projectTotals.totalCO2 < projectTotals.totalCO2 * 1.2 ? '✓' : '⚠',
    'Notes': 'A1-A3 lifecycle stages only'
  },
  {
    'Category': 'CARBON METRICS',
    'Metric': 'Average Carbon Intensity',
    'Value': formatNumber(projectTotals.totalCO2 / projectTotals.totalMass, 3),
    'Unit': 'kg CO2e/kg',
    'Benchmark': '1.5-2.5',
    'Status': (projectTotals.totalCO2 / projectTotals.totalMass) < 2.5 ? '✓' : '⚠',
    'Notes': 'Average across all materials'
  },
  {
    'Category': 'MATERIAL METRICS',
    'Metric': 'Total Material Mass',
    'Value': formatNumber(projectTotals.totalMass, 2),
    'Unit': 'tonnes',
    'Benchmark': 'N/A',
    'Status': '✓',
    'Notes': 'Combined mass of all materials'
  },
  {
    'Category': 'MATERIAL METRICS',
    'Metric': 'Unique Material Types',
    'Value': Object.keys(projectTotals.byMaterial).length,
    'Unit': 'materials',
    'Benchmark': '10-20',
    'Status': '✓',
    'Notes': 'Distinct material classifications'
  },
  {
    'Category': 'VOLUMETRIC DATA',
    'Metric': 'Total Volume',
    'Value': formatNumber(projectTotals.totalVolume, 2),
    'Unit': 'm³',
    'Benchmark': 'N/A',
    'Status': '✓',
    'Notes': 'Where volume data available'
  },
  {
    'Category': 'VOLUMETRIC DATA',
    'Metric': 'Total Area',
    'Value': formatNumber(projectTotals.totalArea, 2),
    'Unit': 'm²',
    'Benchmark': 'N/A',
    'Status': '✓',
    'Notes': 'Where area data available'
  }
];

// Sheet 2: Detailed Elements Analysis - ALL fields from processing
const detailedElements = items.map((item, index) => {
  const data = item.json;
  return {
    // Ranking and identification
    'CO2_Rank': index + 1,
    'Element_Group': data['Element Name'] || data['Type Name'] || 'Unknown',
    'Element_Category': data['Element Category'] || 'Unknown',
    'Element_Type': data['Element Type'] || 'Unknown',
    'Element_Function': data['Element Function'] || 'Unknown',
    'Element_Count': parseInt(data['Element Count']) || 0,

    // Material classification (all 3 standards)
    'Material_EU_Standard': data['Material (EU Standard)'] || 'Unknown',
    'Material_DE_Standard': data['Material (DE Standard)'] || 'Unknown',
    'Material_US_Standard': data['Material (US Standard)'] || 'Unknown',
    'Primary_Material': data['Primary Material'] || 'Unknown',
    'Secondary_Materials': data['Secondary Materials'] || 'None',

    // Quantities and dimensions
    'Quantity_Value': formatNumber(data['Quantity'] || 0, 3),
    'Quantity_Unit': data['Quantity Unit'] || 'piece',
    'Volume_m3': formatNumber(data['Volume (m³)'] || 0, 3),
    'Area_m2': formatNumber(data['Area (m²)'] || 0, 3),
    'Length_mm': formatNumber(data['Length (mm)'] || 0, 0),
    'Width_mm': formatNumber(data['Width (mm)'] || 0, 0),
    'Height_mm': formatNumber(data['Height (mm)'] || 0, 0),
    'Thickness_mm': formatNumber(data['Thickness (mm)'] || 0, 0),

    // Mass and density
    'Material_Density_kg_m3': formatNumber(data['Material Density (kg/m³)'] || 0, 0),
    'Element_Mass_kg': formatNumber(data['Element Mass (kg)'] || 0, 2),
    'Element_Mass_tonnes': formatNumber(data['Element Mass (tonnes)'] || 0, 3),

    // CO2 emissions data
    'CO2_Factor_kg_CO2_per_kg': formatNumber(data['CO2 Factor (kg CO2e/kg)'] || 0, 3),
    'Total_CO2_kg': formatNumber(data['Total CO2 (kg CO2e)'] || 0, 2),
    'Total_CO2_tonnes': formatNumber(data['Total CO2 (tonnes CO2e)'] || 0, 3),
    'CO2_per_Element_kg': formatNumber(data['CO2 per Element (kg CO2e)'] || 0, 2),
    'CO2_Intensity': formatNumber(data['CO2 Intensity'] || 0, 3),
    'CO2_Percent_of_Total': formatNumber(data['CO2 % of Total'] || 0, 2),

    // Impact and quality metrics
    'Impact_Category': data['Impact Category'] || 'Unknown',
    'Lifecycle_Stage': data['Lifecycle Stage'] || 'A1-A3',
    'Data_Source': data['Data Source'] || 'Industry average',

    // Confidence scores
    'Overall_Confidence_%': parseInt(data['Overall Confidence (%)']) || 0,
    'Material_Confidence_%': parseInt(data['Material Confidence (%)']) || 0,
    'Quantity_Confidence_%': parseInt(data['Quantity Confidence (%)']) || 0,
    'CO2_Confidence_%': parseInt(data['CO2 Confidence (%)']) || 0,
    'Data_Quality': data['Data Quality'] || 'unknown',

    // Analysis metadata
    'Calculation_Method': data['Calculation Method'] || 'Not specified',
