// Parse and enrich AI response with all necessary data
const aiResponse = $input.first().json.output || $input.first().json.response || $input.first().json.text || $input.first().json;
const originalData = $node["Prepare Enhanced Prompts"].json._originalGroupedData || $node["Prepare Enhanced Prompts"].json;

try {
  // Extract JSON from response
  let jsonStr = aiResponse;
  if (typeof jsonStr === 'string') {
    const jsonMatch = jsonStr.match(/```json\n?([\s\S]*?)\n?```/) || jsonStr.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      jsonStr = jsonMatch[1] || jsonMatch[0];
    }
  }

  const analysis = typeof jsonStr === 'string' ? JSON.parse(jsonStr) : jsonStr;

  // Calculate additional metrics - use total CO2 for group
  const co2_kg = analysis.co2_analysis?.total_co2_kg || 0;
  const co2_tonnes = co2_kg / 1000;
  const elementCount = parseFloat(originalData['Element Count']) || 1;
  const co2_per_element = co2_kg / elementCount;

  // Determine impact category
  let impactCategory = 'Unknown';
  const co2Factor = analysis.co2_analysis?.co2_factor_kg_co2_per_kg || 0;
  if (co2Factor < 0) {
    impactCategory = 'Carbon Negative (Storage)';
  } else if (co2Factor <= 0.5) {
    impactCategory = 'Very Low Impact';
  } else if (co2Factor <= 1.0) {
    impactCategory = 'Low Impact';
  } else if (co2Factor <= 2.0) {
    impactCategory = 'Medium Impact';
  } else if (co2Factor <= 5.0) {
    impactCategory = 'High Impact';
  } else {
    impactCategory = 'Very High Impact';
  }

  // Create comprehensive output record
  return [{
    json: {
      // Original data
      ...originalData,

      // Element identification
      'Element Name': analysis.element_identification?.name || originalData['Type Name'] || 'Unknown',
      'Element Category': analysis.element_identification?.category || originalData['Category'] || 'Unknown',
      'Element Type': analysis.element_identification?.type || 'Unknown',
      'Element Function': analysis.element_identification?.function || 'Unknown',

