      'Material (US Standard)': analysis.material_classification?.us || 'Unknown',
      'Primary Material': analysis.material_classification?.primary_material || 'Unknown',
      'Secondary Materials': (analysis.material_classification?.secondary_materials || []).join(', ') || 'None',

      // Quantities
      'Quantity': analysis.quantities?.value || 0,
      'Quantity Unit': analysis.quantities?.unit || 'piece',
      'Calculation Method': analysis.quantities?.calculation_method || 'Not specified',

      // Dimensions (from raw data) - these are totals
      'Length (mm)': analysis.quantities?.raw_dimensions?.length || originalData['Length'] || 0,
      'Width (mm)': analysis.quantities?.raw_dimensions?.width || originalData['Width'] || 0,
      'Height (mm)': analysis.quantities?.raw_dimensions?.height || originalData['Height'] || 0,
      'Thickness (mm)': analysis.quantities?.raw_dimensions?.thickness || originalData['Thickness'] || 0,
      'Area (m²)': analysis.quantities?.raw_dimensions?.area || originalData['Area'] || 0,
      'Volume (m³)': analysis.quantities?.raw_dimensions?.volume || originalData['Volume'] || 0,

      // CO2 Analysis
      'Material Density (kg/m³)': analysis.co2_analysis?.density_kg_m3 || 0,
      'Element Mass (kg)': analysis.co2_analysis?.mass_kg || 0,
      'Element Mass (tonnes)': (analysis.co2_analysis?.mass_kg || 0) / 1000,
      'CO2 Factor (kg CO2e/kg)': analysis.co2_analysis?.co2_factor_kg_co2_per_kg || 0,
      'Total CO2 (kg CO2e)': co2_kg,
      'Total CO2 (tonnes CO2e)': co2_tonnes,
      'CO2 per Element (kg CO2e)': co2_per_element,
      'CO2 Intensity': analysis.co2_analysis?.co2_intensity_kg_per_unit || 0,
      'Lifecycle Stage': analysis.co2_analysis?.lifecycle_stage || 'A1-A3',
      'Data Source': analysis.co2_analysis?.data_source || 'Industry average',
      'Impact Category': impactCategory,

      // Confidence scores
      'Overall Confidence (%)': analysis.confidence?.overall_score || 0,
