            <p>
                Analysis reveals <strong>${projectTotals.totalCO2.toFixed(1)} tonnes CO2e</strong> of embodied carbon across 
                ${projectTotals.totalElements.toLocaleString()} building elements. The assessment identifies ${highImpactItems} 
                high-impact element groups contributing over 5% each to total emissions, presenting clear optimization opportunities.
            </p>
        </div>

        <!-- KPI Cards -->
        <div class="kpi-grid">
            <div class="kpi-card primary">
                <div class="kpi-value">${projectTotals.totalCO2.toFixed(0)}</div>
                <div class="kpi-label">Total CO2 Emissions</div>
                <div class="kpi-change">tonnes CO2e</div>
            </div>
            <div class="kpi-card secondary">
                <div class="kpi-value">${((topMaterial[1].co2 / projectTotals.totalCO2) * 100).toFixed(0)}%</div>
                <div class="kpi-label">${topMaterial[0]} Impact</div>
                <div class="kpi-change">of total</div>
            </div>
            <div class="kpi-card primary">
                <div class="kpi-value">${(projectTotals.totalCO2 / projectTotals.totalMass).toFixed(1)}</div>
                <div class="kpi-label">Average Intensity</div>
                <div class="kpi-change">kg CO2e/kg</div>
            </div>
            <div class="kpi-card secondary">
                <div class="kpi-value">${Object.keys(projectTotals.byMaterial).length}</div>
                <div class="kpi-label">Material Types</div>
                <div class="kpi-change">identified</div>
            </div>
        </div>

        <!-- Charts Section -->
        <div class="charts-grid">
            <!-- Pie Chart - Material Distribution -->
            <div class="chart-container">
                <div class="chart-header">
                    <h3>CO2 Emissions by Material Type</h3>
                    <p>Proportional distribution of carbon impact</p>
                </div>
                <div class="chart-wrapper small">
                    <canvas id="materialPieChart"></canvas>
                </div>
            </div>

            <!-- Donut Chart - Impact Concentration -->
            <div class="chart-container">
                <div class="chart-header">
                    <h3>Impact Concentration Analysis</h3>
                    <p>Top 5 vs. remaining elements</p>
                </div>
                <div class="chart-wrapper small">
                    <canvas id="concentrationChart"></canvas>
                </div>
            </div>

            <!-- Bar Chart - Top Contributors -->
            <div class="chart-container full-width">
                <div class="chart-header">
                    <h3>Top 10 Carbon Contributors</h3>
                    <p>Individual element groups ranked by CO2 emissions</p>
                </div>
                <div class="chart-wrapper">
                    <canvas id="topContributorsBar"></canvas>
                </div>
            </div>
        </div>

        <!-- Key Insight -->
        <div class="insight-section">
            <h3>Primary Finding</h3>
            <p>
                <span class="insight-highlight">${topMaterial[0]}</span> represents the largest carbon impact at 
                <span class="insight-highlight">${topMaterial[1].co2.toFixed(1)} tonnes CO2e</span>, accounting for 
                <span class="insight-highlight">${((topMaterial[1].co2 / projectTotals.totalCO2) * 100).toFixed(0)}%</span> 
                of total emissions across ${topMaterial[1].elements.toLocaleString()} elements. This concentration presents 
                the primary opportunity for carbon reduction through material substitution or design optimization.
            </p>
        </div>

        <!-- Top Contributors Table -->
        <div class="table-container">
            <div class="table-header">
                <h3>Top 10 Carbon Contributors</h3>
            </div>
            <table>
                <thead>
                    <tr>
                        <th style="width: 60px;">Rank</th>
                        <th>Element Group</th>
                        <th>Material Type</th>
                        <th style="width: 80px;">Quantity</th>
                        <th style="width: 100px;">CO2 (tonnes)</th>
                        <th style="width: 150px;">% of Total</th>
                        <th style="width: 80px;">Priority</th>
                    </tr>
                </thead>
                <tbody>
                    ${items.slice(0, 10).map((item, index) => {
                        const data = item.json;
                        const co2Percent = parseFloat(data['CO2 % of Total']);
                        let impactClass = 'impact-low';
                        let impactText = 'LOW';

                        if (co2Percent >= 10) {
                            impactClass = 'impact-critical';
                            impactText = 'CRITICAL';
                        } else if (co2Percent >= 5) {
                            impactClass = 'impact-high';
                            impactText = 'HIGH';
                        } else if (co2Percent >= 2) {
                            impactClass = 'impact-medium';
                            impactText = 'MEDIUM';
                        }

                        return `
                    <tr>
                        <td>${index + 1}</td>
                        <td class="value-highlight">${data['Element Name']}</td>
                        <td>${data['Material (EU Standard)']}</td>
                        <td>${data['Element Count']}</td>
                        <td class="value-highlight">${parseFloat(data['Total CO2 (tonnes CO2e)']).toFixed(2)}</td>
                        <td>
                            ${co2Percent.toFixed(1)}%
                            <div class="progress-bar">
                                <div class="progress-fill" style="width: ${Math.min(co2Percent * 5, 100)}%"></div>
                            </div>
                        </td>
                        <td><span class="${impactClass}">${impactText}</span></td>
                    </tr>`;
                    }).join('')}
                </tbody>
            </table>
        </div>

        <!-- Material Analysis Table -->
        <div class="table-container">
            <div class="table-header">
                <h3>Material Impact Analysis</h3>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Material Classification</th>
                        <th style="width: 100px;">Elements</th>
                        <th style="width: 100px;">Mass (t)</th>
                        <th style="width: 100px;">CO2 (t)</th>
                        <th style="width: 150px;">% of Total</th>
                        <th style="width: 120px;">Intensity</th>
                    </tr>
                </thead>
                <tbody>
                    ${Object.entries(projectTotals.byMaterial)
                        .sort((a, b) => b[1].co2 - a[1].co2)
                        .slice(0, 6)
                        .map(([material, data]) => {
                            const percent = (data.co2 / projectTotals.totalCO2) * 100;
                            return `
                    <tr>
                        <td class="value-highlight">${material}</td>
                        <td>${data.elements.toLocaleString()}</td>
                        <td>${data.mass.toFixed(1)}</td>
                        <td class="value-highlight">${data.co2.toFixed(1)}</td>
                        <td>
                            ${percent.toFixed(1)}%
                            <div class="progress-bar">
                                <div class="progress-fill" style="width: ${Math.min(percent * 3, 100)}%"></div>
                            </div>
                        </td>
                        <td>${(data.co2 / data.mass).toFixed(2)} kg/kg</td>
                    </tr>`;
                        }).join('')}
                </tbody>
            </table>
        </div>

        <!-- Cumulative Impact Chart - moved here after Material Analysis -->
        <div class="charts-grid">
            <div class="chart-container full-width">
                <div class="chart-header">
                    <h3>Cumulative Carbon Impact</h3>
                    <p>Pareto analysis showing concentration of emissions</p>
                </div>
                <div class="chart-wrapper">
                    <canvas id="cumulativeChart"></canvas>
                </div>
            </div>
        </div>

        <!-- Action Items -->
        <div class="action-box">
            <h4>Recommended Actions</h4>
            <ul class="action-list">
                <li>Prioritize ${topMaterial[0]} optimization - potential ${(topMaterial[1].co2 * 0.2).toFixed(0)}t CO2e reduction with 20% improvement</li>
                <li>Review specification for ${highImpactItems} high-impact element groups (>5% each of total)</li>
                <li>Investigate low-carbon alternatives for top 3 materials: ${Object.entries(projectTotals.byMaterial).sort((a,b) => b[1].co2 - a[1].co2).slice(0,3).map(([m]) => m).join(', ')}</li>
                <li>Focus on elements exceeding project average intensity of ${(projectTotals.totalCO2 / projectTotals.totalMass).toFixed(1)} kg CO2e per kg material</li>
            </ul>
        </div>

        <!-- Footer -->
        <div class="footer">
            <p>
                <span class="logo">Carbon Footprint Analysis</span> • 
                Embodied Carbon Assessment (A1-A3) • 
                Generated ${new Date().toLocaleString()}
            </p>
            <p style="margin-top: 8px;">
                Powered by DataDrivenConstruction.io • 
                Detailed calculations available in accompanying Excel workbook
            </p>
        </div>
    </div>

    <script>
        // Chart.js global configuration
        Chart.defaults.font.family = "'Arial', 'Helvetica Neue', sans-serif";
        Chart.defaults.font.size = 11;

        // Professional color palette
        const colors = {
            primary: ['#0061a0', '#00a19a', '#460073', '#009a44', '#ff7043', '#ffa726'],
            secondary: ['#4fc3f7', '#81c784', '#ba68c8', '#ffb74d', '#e57373', '#64b5f6'],
            gradient: ['rgba(0, 97, 160, 0.8)', 'rgba(0, 161, 154, 0.8)', 'rgba(70, 0, 115, 0.8)']
        };

        // Material Pie Chart
        const materialData = ${JSON.stringify(Object.entries(projectTotals.byMaterial)
            .sort((a, b) => b[1].co2 - a[1].co2)
            .slice(0, 6)
            .map(([material, data]) => ({
                label: material,
                value: data.co2
            })))};

        new Chart(document.getElementById('materialPieChart'), {
            type: 'pie',
            data: {
                labels: materialData.map(d => d.label),
                datasets: [{
                    data: materialData.map(d => d.value.toFixed(1)),
                    backgroundColor: colors.primary,
                    borderColor: '#ffffff',
                    borderWidth: 2
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                    legend: {
                        position: 'right',
                        labels: {
                            padding: 10,
                            usePointStyle: true,
                            font: { size: 11 }
                        }
                    },
                    tooltip: {
                        callbacks: {
                            label: function(context) {
                                const total = context.dataset.data.reduce((a, b) => parseFloat(a) + parseFloat(b), 0);
                                const percent = ((context.parsed / total) * 100).toFixed(1);
                                return context.label + ': ' + context.parsed + 't (' + percent + '%)';
                            }
                        }
                    }
                }
            }
        });

        // Concentration Donut Chart
        const top5Total = ${items.slice(0, 5).reduce((sum, item) => 
            sum + parseFloat(item.json['Total CO2 (tonnes CO2e)']), 0).toFixed(1)};
        const remainingTotal = ${(projectTotals.totalCO2 - items.slice(0, 5).reduce((sum, item) => 
            sum + parseFloat(item.json['Total CO2 (tonnes CO2e)']), 0)).toFixed(1)};

        new Chart(document.getElementById('concentrationChart'), {
            type: 'doughnut',
            data: {
                labels: ['Top 5 Elements', 'Remaining Elements'],
                datasets: [{
                    data: [top5Total, remainingTotal],
                    backgroundColor: ['#0061a0', '#e0e0e0'],
                    borderColor: '#ffffff',
                    borderWidth: 2
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                cutout: '60%',
                plugins: {
                    legend: {
                        position: 'bottom',
                        labels: {
                            padding: 15,
                            usePointStyle: true,
                            font: { size: 11 }
                        }
                    },
                    tooltip: {
                        callbacks: {
                            label: function(context) {
                                const percent = ((context.parsed / ${projectTotals.totalCO2}) * 100).toFixed(1);
                                return context.label + ': ' + context.parsed + 't (' + percent + '%)';
                            }
                        }
                    }
                }
            }
        });

        // Top Contributors Bar Chart
        const topItems = ${JSON.stringify(items.slice(0, 10).map(item => ({
            name: item.json['Element Name'],
            co2: parseFloat(item.json['Total CO2 (tonnes CO2e)'])
        })))};

        new Chart(document.getElementById('topContributorsBar'), {
            type: 'bar',
            data: {
                labels: topItems.map(d => d.name),
                datasets: [{
                    label: 'CO2 Emissions (tonnes)',
                    data: topItems.map(d => d.co2),
                    backgroundColor: topItems.map((d, i) => {
                        const percent = (d.co2 / ${projectTotals.totalCO2}) * 100;
                        if (percent >= 10) return '#dc3545';
                        if (percent >= 5) return '#ff7043';
                        if (percent >= 2) return '#ffa726';
                        return '#66bb6a';
                    }),
                    borderColor: 'rgba(0, 0, 0, 0.1)',
                    borderWidth: 1
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                indexAxis: 'y',
                plugins: {
                    legend: {
                        display: false
                    },
                    tooltip: {
                        callbacks: {
                            afterLabel: function(context) {
                                const percent = ((context.parsed.x / ${projectTotals.totalCO2}) * 100).toFixed(1);
                                return percent + '% of total';
                            }
                        }
                    }
                },
                scales: {
                    x: {
                        grid: {
                            color: 'rgba(0, 0, 0, 0.05)'
                        },
                        ticks: {
                            callback: function(value) {
                                return value + 't';
                            }
                        }
                    },
                    y: {
                        grid: {
                            display: false
                        },
                        ticks: {
                            font: {
                                size: 10
                            }
                        }
                    }
                }
            }
        });

        // Cumulative Impact Area Chart
        const allItems = ${JSON.stringify(items.map(item => ({
            name: item.json['Element Name'],
            co2: parseFloat(item.json['Total CO2 (tonnes CO2e)'])
        })))};

        let cumulative = 0;
        const cumulativeData = allItems.map((item, index) => {
            cumulative += item.co2;
            return {
                x: index + 1,
                y: cumulative,
                percent: (cumulative / ${projectTotals.totalCO2}) * 100
            };
        });

        new Chart(document.getElementById('cumulativeChart'), {
            type: 'line',
            data: {
                labels: cumulativeData.map(d => 'Element ' + d.x),
                datasets: [{
                    label: 'Cumulative CO2',
                    data: cumulativeData.map(d => d.y),
                    borderColor: '#0061a0',
                    backgroundColor: 'rgba(0, 97, 160, 0.1)',
                    fill: true,
                    tension: 0.4,
                    pointRadius: 0,
                    pointHoverRadius: 4
                }, {
                    label: 'Cumulative %',
                    data: cumulativeData.map(d => d.percent),
                    borderColor: '#00a19a',
                    backgroundColor: 'transparent',
                    yAxisID: 'y1',
                    borderDash: [5, 5],
                    fill: false,
                    tension: 0.4,
                    pointRadius: 0,
                    pointHoverRadius: 4
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                interaction: {
                    mode: 'index',
                    intersect: false
                },
                plugins: {
                    legend: {
                        position: 'top',
                        labels: {
                            usePointStyle: true,
                            padding: 15
                        }
                    },
                    tooltip: {
                        callbacks: {
                            title: function(context) {
                                const index = context[0].dataIndex;
                                if (index < allItems.length) {
                                    return allItems[index].name;
                                }
                                return 'Element ' + (index + 1);
                            }
                        }
                    }
                },
                scales: {
                    x: {
                        display: false
                    },
                    y: {
                        type: 'linear',
                        display: true,
                        position: 'left',
                        grid: {
                            color: 'rgba(0, 0, 0, 0.05)'
                        },
                        ticks: {
                            callback: function(value) {
                                return value.toFixed(0) + 't';
                            }
                        },
                        title: {
                            display: true,
                            text: 'Cumulative CO2 (tonnes)'
                        }
                    },
                    y1: {
                        type: 'linear',
                        display: true,
                        position: 'right',
                        grid: {
                            drawOnChartArea: false
                        },
                        ticks: {
                            callback: function(value) {
                                return value.toFixed(0) + '%';
                            }
                        },
                        title: {
                            display: true,
                            text: 'Cumulative Percentage'
                        }
                    }
                }
            }
        });
    </script>
</body>
</html>`;

return [{
  json: {
    html: html,
    reportType: 'mckinsey-accenture-professional',
    timestamp: new Date().toISOString()
  }
}];

// Convert HTML to binary for file output
const html = $input.first().json.html;
const fileName = `CO2_Analysis_Report_${new Date().toISOString().slice(0,10)}.html`;

// Return with 'data' as the default binary field name
return [{
  json: {
    fileName: fileName  // Also pass filename in json for backup
  },
  binary: {
    data: {
      data: Buffer.from(html).toString('base64'),
      mimeType: 'text/html',
      fileName: fileName,
      fileExtension: 'html'
    }
  }
}];
