const { QdrantVectorStore } = require("@langchain/qdrant");
const { OllamaEmbeddings } = require("@langchain/community/embeddings/ollama");

const OLLAMA_BASE_URL = $env.OLLAMA_BASE_URL || "http://localhost:11434";
const QDRANT_BASE_URL = $env.QDRANT_BASE_URL || "http://localhost:6333";
const QDRANT_COLLECTION = $env.QDRANT_COLLECTION || "audit-docs";
const EMBED_MODEL = $env.OLLAMA_EMBED_MODEL || "nomic-embed-text";

const embeddings = new OllamaEmbeddings({ model: EMBED_MODEL, baseUrl: OLLAMA_BASE_URL });
const vectorStore = await QdrantVectorStore.fromExistingCollection(embeddings, { url: QDRANT_BASE_URL, collectionName: QDRANT_COLLECTION });

const items = this.getInputData();
const fileIdToDelete = items[0].json.body.spDocumentId;

const filter = { must: [ { key: "metadata.file_id", match: { value: fileIdToDelete } } ] };

try {
  if (vectorStore?.client?.delete) {
    await vectorStore.client.delete(QDRANT_COLLECTION, { filter });
  }
} catch (e) {
  // Non-fatal: continue import/index even if delete fails
  this.logger?.warn?.(`Qdrant delete skipped/failed: ${e?.message || e}`);
}

return items.map(item => ({ json: { ...item.json, file_id: fileIdToDelete }, binary: item.binary }));

