const output = [];

for (const item of items) {
  const data = item.json;

  // Case 1: service snapshot (object with namespace/service keys)
  if (data && Object.keys(data).some(k => k.includes('/'))) {
    for (const [svc, count] of Object.entries(data)) {
      output.push({
        type: "service",
        name: svc,
        count: count
      });
    }
    continue;
  }

  // Case 2: already-normalized pod_restart object
  if (data?.type === "pod_restart") {
    output.push({
      type: "pod_restart",
      namespace: data.namespace,
      pod: data.pod,
      container: data.container || null,
      value: data.value ? Number(data.value) : 0
    });
    continue;
  }

  // Case 3: raw Prometheus format with data.result array
  if (data?.data?.result) {
    for (const r of data.data.result) {
      const ns = r.metric.namespace;
      const pod = r.metric.pod;
      const container = r.metric.container || null;
      const reason = r.metric.reason || null;
      const val = parseFloat(r.value[1]);

      // classify
      let issueType = "pod";
      if (reason) issueType = "pod_waiting_reason";
      if (r.metric.__name__?.includes("ready") && val === 0) issueType = "pod_not_ready";
      if (r.metric.__name__?.includes("restarts_total")) issueType = "pod_restart";

      // NEW: detect Pending pods
      if (
        r.metric.__name__?.includes("pod_status_phase") &&
        r.metric.phase === "Pending" &&
        val > 0
      ) {
        issueType = "pod_pending";
      }

      output.push({
        type: issueType,
        namespace: ns,
        pod,
        container,
        reason,
        phase: r.metric.phase || null,
        value: val
      });
    }
  }
}

return output.map(o => ({ json: o }));

const data = items.map(i => i.json);

// Group data
let services = [];
let podsNotReady = [];
let podsWaiting = [];
let podsRestarts = [];
let podsPending = [];   // NEW group

for (const d of data) {
  if (d.type === "service") {
    services.push(d);
  }
  if (d.type === "pod_not_ready" && d.value === 0) {
    podsNotReady.push(d);
  }
  if (d.type === "pod_waiting_reason" && d.value > 0) {
    podsWaiting.push(d);
  }
  if (d.type === "pod_restart" && d.value > 0) {
    podsRestarts.push(d);
  }
  if (d.type === "pod_pending" && d.value > 0) {   // NEW condition
    podsPending.push(d);
  }
}

// ---- Service comparison (detect changes) ----
let serviceChanges = [];
if (services.length > 0) {
  // Split into two snapshots (prev & curr)
  const half = services.length / 2;
  const prev = services.slice(0, half);
  const curr = services.slice(half);

  // Build dicts
  const prevMap = {};
  prev.forEach(s => prevMap[s.name] = s.count);
  const currMap = {};
  curr.forEach(s => currMap[s.name] = s.count);

  for (const svc in currMap) {
    const current = currMap[svc];
    const previous = prevMap[svc] ?? 0;

    if (current !== previous) {
      if (previous === 0 && current > 0) {
        serviceChanges.push(`ðŸ†• Service \`${svc}\` appeared with *${current}* endpoints`);
      } else if (current === 0) {
        serviceChanges.push(`âŒ Service \`${svc}\` lost all endpoints (was *${previous}*)`);
      } else {
        serviceChanges.push(`âš ï¸ Service \`${svc}\` endpoint count changed from *${previous}* â†’ *${current}*`);
      }
    }
  }
}

// ---- Slack message builder ----
let msg = ":mag: *Kubernetes Cluster Health Update*\n\n";

// Service section
msg += ":gear: *Service Discovery Changes:*\n";
if (serviceChanges.length > 0) {
  msg += serviceChanges.map(c => `â€¢ ${c}`).join("\n") + "\n\n";
} else {
  msg += "âœ… No service endpoint changes detected\n\n";
}

// Pods Not Ready
if (podsNotReady.length > 0) {
  msg += ":warning: *Pods Not Ready:*\n";
  msg += podsNotReady.map(p => `â€¢ [${p.namespace}] \`${p.pod}\` (container: ${p.container})`).join("\n") + "\n\n";
}

// Pods Pending (NEW)
if (podsPending.length > 0) {
  msg += ":hourglass_flowing_sand: *Pods Pending Scheduling:*\n";
  msg += podsPending.map(p => `â€¢ [${p.namespace}] \`${p.pod}\` (phase: ${p.phase})`).join("\n") + "\n\n";
}

// Pods Waiting
if (podsWaiting.length > 0) {
  msg += ":fire: *Pods in Waiting State:*\n";
  msg += podsWaiting.map(p => `â€¢ [${p.namespace}] \`${p.pod}\` â€“ reason: *${p.reason}*`).join("\n") + "\n\n";
}

// Pods Restarts
if (podsRestarts.length > 0) {
  msg += ":repeat: *Pods with Restart Spike (5m):*\n";
  msg += podsRestarts.map(p => `â€¢ [${p.namespace}] \`${p.pod}\` (container: ${p.container}) â€“ restarts: ${p.value}`).join("\n") + "\n\n";
}

return [{ json: { text: msg.trim() } }];

// Map Prometheus results into { "<namespace>/<service>": value }
const out = {};
for (const r of $json["data"]["result"]) {
  const ns = r.metric.namespace;
  const svc = r.metric.service;
  const val = parseFloat(r.value[1]);
  out[`${ns}/${svc}`] = val;
}
return [{ json: out }];

// Map Prometheus results into { "<namespace>/<service>": value }
const out = {};
for (const r of $json["data"]["result"]) {
  const ns = r.metric.namespace;
  const svc = r.metric.service;
  const val = parseFloat(r.value[1]);
  out[`${ns}/${svc}`] = val;
}
return [{ json: out }];

const prom = $json;
const results = [];

if (prom.data && prom.data.result) {
  for (const r of prom.data.result) {
    results.push({
      type: "pod_restart",
      namespace: r.metric.namespace,
      pod: r.metric.pod,
      container: r.metric.container || "unknown",
      value: Math.round(Number(r.value[1])) // round off decimals
    });
  }
}

return results.map(r => ({ json: r }));
