  "analysis": {
    "issue": "Brief problem description",
    "reasoning": "Why this occurred",
    "confidence_level": "High/Medium/Low",
    "error_category": "Authentication/Data Processing/API Limits/Configuration/etc"
  },
  "solution": {
    "primary_fix": "Main solution",
    "alternative_approaches": ["Alt 1", "Alt 2"],
    "implementation_steps": ["Step 1", "Step 2", "Step 3"]
  },
  "prevention": {
    "recommendations": ["Rec 1", "Rec 2"],
    "best_practices": ["Practice 1", "Practice 2"]
