// n8n Code Node: Generate Slack Message for Workflow Errors
// Enhanced to include FreshDesk ticket information from merged input

// Get the merged data from the current input (contains both ticket and analysis data)
const mergedData = $input.first().json;

// Get execution data from the Error Trigger node (previous node in workflow)
const executionData = $('Error Trigger').first().json;

// Debug: Log the input structure
console.log('Merged data:', JSON.stringify(mergedData, null, 2));
console.log('Execution data:', JSON.stringify(executionData, null, 2));

// Extract execution details with safe access
const execution = executionData?.execution || {};
const workflow = executionData?.workflow || {};

// Extract analysis details from merged data - using the clean variable names
const issueText = mergedData?.debugger_analysis_issue || 'No analysis available';
const reasoning = mergedData?.debugger_analysis_reasoning || 'No reasoning provided';
const confidenceLevel = mergedData?.debugger_analysis_level || 'Unknown';
const errorCategory = mergedData?.debugger_analysis_category || 'General';
const primaryFix = mergedData?.debugger_solution || 'No solution provided';
const implementationSteps = mergedData?.debugger_steps || ['No steps available'];
const alternativeApproaches = mergedData?.debugger_approaches || ['No alternatives available'];
const prevention = mergedData?.debugger_prevention || {};

// Extract FreshDesk ticket details from merged data
const ticketId = mergedData?.id || 'Unknown';
const ticketSubject = mergedData?.subject || 'No subject available';
const ticketStatus = mergedData?.status || 'Unknown';
const ticketPriority = mergedData?.priority || 'Unknown';
const ticketCreatedAt = mergedData?.created_at || 'Unknown';
const ticketUrl = ticketId !== 'Unknown' ? `https://qlabs.freshdesk.com/a/tickets/${ticketId}` : '#';

// Set default values for missing properties
const workflowName = mergedData?.workflow_name || workflow?.name || 'Unknown Workflow';
const executionId = execution?.id || 'Unknown';
const executionUrl = mergedData?.workflow_url || execution?.url || '#';
const lastNode = execution?.lastNodeExecuted || 'Unknown Node';
const executionMode = execution?.mode || 'unknown';
const errorMessage = mergedData?.workflow_error_message || execution?.error?.message || 'No error message available';
const errorStack = execution?.error?.stack || '';

const recommendations = prevention?.recommendations || ['No recommendations available'];
const bestPractices = prevention?.best_practices || ['No best practices available'];

// Helper function to format arrays as bullet points
function formatArray(array, prefix = "‚Ä¢ ") {
  if (!Array.isArray(array)) return 'No data available';
  return array.map(item => `${prefix}${item}`).join('\n');
}

// Generate confidence emoji based on level
function getConfidenceEmoji(level) {
  switch(level.toLowerCase()) {
    case 'high': return 'üî¥';
    case 'medium': return 'üü°';
    case 'low': return 'üü¢';
    default: return '‚ö™';
  }
}

// Generate category emoji
function getCategoryEmoji(category) {
  switch(category.toLowerCase()) {
    case 'configuration': return '‚öôÔ∏è';
    case 'authentication': return 'üîê';
    case 'network': return 'üåê';
    case 'data': return 'üìä';
    case 'data processing': return 'üìä';
    case 'timeout': return '‚è∞';
    default: return '‚ùå';
  }
}

// Generate priority emoji
function getPriorityEmoji(priority) {
  switch(priority) {
    case 1: return 'üî•'; // Urgent
    case 2: return 'üü°'; // High
    case 3: return 'üü¢'; // Medium
    case 4: return 'üîµ'; // Low
    default: return '‚ö™';
  }
}

// Generate status text
function getStatusText(status) {
  switch(status) {
    case 2: return 'Open';
    case 3: return 'Pending';
    case 4: return 'Resolved';
    case 5: return 'Closed';
    default: return 'Unknown';
  }
}

// Build the Slack message
const slackMessage = {
  "blocks": [
    {
      "type": "header",
      "text": {
        "type": "plain_text",
        "text": "üö® n8n Workflow Error Alert"
      }
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": `*Workflow:*\n${workflowName}`
        },
        {
          "type": "mrkdwn",
          "text": `*Execution ID:*\n${executionId}`
        },
        {
          "type": "mrkdwn",
          "text": `*Failed Node:*\n${lastNode}`
        },
        {
          "type": "mrkdwn",
          "text": `*Mode:*\n${executionMode}`
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*Error Message:*\n\`\`\`${errorMessage}\`\`\``
      }
    },
    {
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "View Execution"
          },
          "url": executionUrl,
          "style": "primary"
        }
      ]
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*üé´ Support Ticket Created*`
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": `*Ticket ID:*\n#${ticketId}`
        },
        {
          "type": "mrkdwn",
          "text": `*Status:*\n${getStatusText(ticketStatus)}`
        },
        {
          "type": "mrkdwn",
          "text": `*Priority:*\n${getPriorityEmoji(ticketPriority)} Priority ${ticketPriority}`
        },
        {
          "type": "mrkdwn",
          "text": `*Created:*\n${new Date(ticketCreatedAt).toLocaleString()}`
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*Subject:*\n${ticketSubject}`
      }
    },
    {
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "View Ticket"
          },
          "url": ticketUrl,
          "style": "primary"
        }
      ]
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*${getCategoryEmoji(errorCategory)} AI Analysis*`
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": `*Category:*\n${errorCategory}`
        },
        {
          "type": "mrkdwn",
          "text": `*Confidence:*\n${getConfidenceEmoji(confidenceLevel)} ${confidenceLevel}`
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*Issue:*\n${issueText}`
      }
    },
    {
      "type": "section",
      "text": {
