console.log('Raw AI output:', $json.output);

try {
  let jsonString = $json.output;

  // Remove markdown code fences if present
  if (jsonString.includes('```json')) {
    console.log('Removing markdown fences');
    jsonString = jsonString.replace(/```json\n?/g, '').replace(/```\n?$/g, '');
  }

  // Remove any leading "json\n" or similar
  jsonString = jsonString.replace(/^json\n/g, '');

  // Clean up any extra whitespace
  jsonString = jsonString.trim();

  console.log('Cleaned JSON string:', jsonString.substring(0, 200) + '...');

  const output = JSON.parse(jsonString);
  console.log(`Successfully parsed! Found ${output.slides.length} slides`);

  // Return the slides directly from AI (now with tableData support)
  return [{ json: { slides: output.slides } }];

} catch (e) {
  console.error('JSON parsing failed:', e.message);
  console.log('Attempting manual extraction...');

  // Try to extract JSON from between { and }
  const match = $json.output.match(/\{[\s\S]*\}/);
  if (match) {
    try {
      const output = JSON.parse(match[0]);
      console.log('Manual extraction successful!');
      return [{ json: { slides: output.slides } }];
    } catch (e2) {
      console.log('Manual extraction also failed');
    }
  }

  // Last resort: return fallback with table structure for Channel KPIs
  const fallbackSlides = [
    { 
      title: "Executive Snapshot", 
      body: "Weekly performance data is currently being processed. Report will be available shortly." 
    },
    { 
      title: "Channel KPIs", 
      tableData: {
        "Spend": "Data pending",
        "Impressions": "Data pending",
        "Clicks": "Data pending",
        "CTR": "Data pending",
        "CPC": "Data pending",
        "Conversions": "Data pending",
        "CPA": "Data pending",
        "Revenue": "Data pending",
        "ROAS": "Data pending",
        "WoW Change": "Data pending"
      }
    },
    { 
      title: "Top Campaigns", 
      body: "Campaign performance analysis is in progress. Top-performing campaigns will be highlighted once data processing is complete." 
    },
    { 
      title: "Under-performers", 
      body: "Performance analysis not available at this time. Underperforming campaigns will be identified in the next report." 
    },
    { 
      title: "Action Recos", 
      body: "Strategic recommendations will be provided once performance data analysis is complete. Please check the next scheduled report." 
    }
  ];

  return [{ json: { slides: fallbackSlides } }];
}

// Get the data from the Combine Data node
const presentationId = $json.presentationId;
const slides = $json.slides;

const requests = [
  // Delete the default slide first
  {
    deleteObject: { objectId: 'p' }
  }
];

// Create exactly 5 slides with content
slides.forEach((slide, index) => {
  const slideId = `slide_${index + 1}`;
  const titleId = `${slideId}_title`;

  // Create slide
  requests.push({
    createSlide: {
      objectId: slideId,
      slideLayoutReference: { predefinedLayout: 'TITLE_AND_BODY' },
      placeholderIdMappings: [
        { layoutPlaceholder: { type: 'TITLE', index: 0 }, objectId: titleId },
        { layoutPlaceholder: { type: 'BODY', index: 0 }, objectId: `${slideId}_body` }
      ]
    }
  });

  // Add title
  requests.push({
    insertText: { objectId: titleId, insertionIndex: 0, text: slide.title }
  });

  // Handle Channel KPIs slide with table
  if (slide.title === "Channel KPIs" && slide.tableData) {
    const tableId = `${slideId}_table`;

    // Create table (2 columns, 10 rows for the metrics)
    requests.push({
      createTable: {
  objectId: tableId,
  elementProperties: {
    pageObjectId: slideId,
    size: { width: { magnitude: 350, unit: 'PT' }, height: { magnitude: 200, unit: 'PT' } },
    transform: {
      scaleX: 1, scaleY: 1, translateX: 185, translateY: 80, unit: 'PT'
    }
  },
  rows: 10,
  columns: 2
}
    });

    // Populate table with data
    const metrics = Object.entries(slide.tableData);
    metrics.forEach(([metric, value], rowIndex) => {
      // Insert metric name in first column
      requests.push({
        insertText: {
          objectId: tableId,
          cellLocation: { rowIndex: rowIndex, columnIndex: 0 },
          insertionIndex: 0,
          text: metric
        }
      });

      // Insert metric value in second column
      requests.push({
        insertText: {
          objectId: tableId,
          cellLocation: { rowIndex: rowIndex, columnIndex: 1 },
          insertionIndex: 0,
          text: value
        }
      });
    });

  } else {
    // For all other slides, use regular text insertion
    const bodyId = `${slideId}_body`;
    requests.push({
      insertText: { objectId: bodyId, insertionIndex: 0, text: slide.body }
    });
  }
});

return [{
  json: {
    presentationId: presentationId,
    batchUpdateBody: { requests: requests }
  }
}];
