// Parse Google Sheets data and prepare for crypto API calls
const items = [];
const inputData = $input.all();

// Skip header row, process data rows
for (let i = 1; i < inputData.length; i++) {
  const row = inputData[i].json;
  if (row.A && row.B && row.C) { // Check if required fields exist

    // Convert crypto symbol to CoinGecko format
    let coinId = row.A.toLowerCase();

    // Handle common crypto pairs and convert to CoinGecko IDs
    const cryptoMapping = {
      'btc': 'bitcoin',
      'btc/usdt': 'bitcoin',
      'btc-usdt': 'bitcoin',
      'bitcoin': 'bitcoin',
      'eth': 'ethereum', 
      'eth/usdt': 'ethereum',
      'eth-usdt': 'ethereum',
      'ethereum': 'ethereum',
      'bnb': 'binancecoin',
      'bnb/usdt': 'binancecoin',
      'bnb-usdt': 'binancecoin',
      'ada': 'cardano',
      'ada/usdt': 'cardano',
      'ada-usdt': 'cardano',
      'dot': 'polkadot',
      'dot/usdt': 'polkadot',
      'dot-usdt': 'polkadot',
      'sol': 'solana',
      'sol/usdt': 'solana',
      'sol-usdt': 'solana',
      'matic': 'matic-network',
      'matic/usdt': 'matic-network',
      'matic-usdt': 'matic-network',
      'link': 'chainlink',
      'link/usdt': 'chainlink',
      'link-usdt': 'chainlink',
      'avax': 'avalanche-2',
      'avax/usdt': 'avalanche-2',
      'avax-usdt': 'avalanche-2'
    };

    const mappedCoinId = cryptoMapping[coinId] || coinId.split('/')[0].split('-')[0];

    items.push({
      json: {
        original_symbol: row.A,
        coin_id: mappedCoinId,
        upper_limit: parseFloat(row.B),
        lower_limit: parseFloat(row.C),
        direction: row.D || 'both',
        cooldown_minutes: parseInt(row.E) || 10, // Shorter cooldown for crypto
        last_alert_price: parseFloat(row.F) || 0,
        last_alert_time: row.G || ''
      }
    });
  }
}

return items;

// Process crypto price and check alerts
const items = [];
const currentTime = new Date();

for (const item of $input.all()) {
  const cryptoData = item.json;

  let priceResponse;
  try {
    priceResponse = JSON.parse(cryptoData.body || '{}');
  } catch (e) {
    continue; // Skip if invalid JSON
  }

  const coinId = cryptoData.coin_id;
  const coinPriceData = priceResponse[coinId];

  if (!coinPriceData || !coinPriceData.usd) {
    continue; // Skip if no price data
  }

  const currentPrice = parseFloat(coinPriceData.usd);
  const change24h = coinPriceData.usd_24h_change || 0;
  const marketCap = coinPriceData.usd_market_cap || 0;

  const symbol = cryptoData.original_symbol;
  const upperLimit = cryptoData.upper_limit;
  const lowerLimit = cryptoData.lower_limit;
  const direction = cryptoData.direction;
  const cooldownMinutes = cryptoData.cooldown_minutes;
  const lastAlertPrice = cryptoData.last_alert_price;
  const lastAlertTime = cryptoData.last_alert_time;

  // Check cooldown period
  let shouldCheckAlert = true;
  if (lastAlertTime) {
    const lastAlert = new Date(lastAlertTime);
    const timeDiff = (currentTime - lastAlert) / (1000 * 60); // minutes
    if (timeDiff < cooldownMinutes) {
      shouldCheckAlert = false;
    }
  }

  let alertTriggered = false;
  let alertMessage = '';
  let alertType = '';

  if (shouldCheckAlert) {
    // Check upper limit (BREAKOUT)
    if ((direction === 'both' || direction === 'above') && currentPrice > upperLimit) {
      alertTriggered = true;
      alertType = 'upper';
      const pumpPercentage = ((currentPrice - upperLimit) / upperLimit * 100).toFixed(2);
      alertMessage = `üöÄ CRYPTO BREAKOUT ALERT! üöÄ\n\n` +
                    `üíé ${symbol.toUpperCase()}\n` +
                    `üìà Current Price: $${currentPrice.toLocaleString()}\n` +
                    `üéØ Upper Target: $${upperLimit.toLocaleString()}\n` +
                    `‚ö° Pump: +${pumpPercentage}% above target!\n` +
                    `üìä 24h Change: ${change24h >= 0 ? '+' : ''}${change24h.toFixed(2)}%\n` +
                    `üí∞ Market Cap: $${(marketCap / 1000000000).toFixed(2)}B\n` +
                    `‚è∞ ${currentTime.toLocaleString()}\n\n` +
                    `üî• BULLISH MOMENTUM DETECTED! üî•`;
    }
    // Check lower limit (BREAKDOWN) 
    else if ((direction === 'both' || direction === 'below') && currentPrice < lowerLimit) {
      alertTriggered = true;
      alertType = 'lower';
      const dumpPercentage = ((lowerLimit - currentPrice) / lowerLimit * 100).toFixed(2);
      alertMessage = `üìâ CRYPTO BREAKDOWN ALERT! üìâ\n\n` +
                    `üíé ${symbol.toUpperCase()}\n` +
                    `üìâ Current Price: $${currentPrice.toLocaleString()}\n` +
                    `üéØ Lower Support: $${lowerLimit.toLocaleString()}\n` +
                    `‚ö†Ô∏è Dump: -${dumpPercentage}% below support!\n` +
                    `üìä 24h Change: ${change24h >= 0 ? '+' : ''}${change24h.toFixed(2)}%\n` +
                    `üí∞ Market Cap: $${(marketCap / 1000000000).toFixed(2)}B\n` +
                    `‚è∞ ${currentTime.toLocaleString()}\n\n` +
                    `üö® BEARISH MOMENTUM DETECTED! üö®`;
    }
  }

  if (alertTriggered) {
    items.push({
      json: {
        symbol: symbol,
        coin_id: coinId,
        current_price: currentPrice,
        upper_limit: upperLimit,
        lower_limit: lowerLimit,
        change_24h: change24h,
        market_cap: marketCap,
        alert_type: alertType,
        alert_message: alertMessage,
        timestamp: currentTime.toISOString(),
        // Data for updating Google Sheet
        row_data: {
          symbol: symbol,
          upper_limit: upperLimit,
          lower_limit: lowerLimit,
          direction: direction,
          cooldown_minutes: cooldownMinutes,
          last_alert_price: currentPrice,
          last_alert_time: currentTime.toISOString()
        }
      }
    });
  }
}

return items;
