parameters.jsCode:
const evalResult = $input.first().json.output
return [
  {
    json: {
      full_name: $('Profile Analyzer Agent').first().json.output.Profile.full_name,
      email: $('Profile Analyzer Agent').first().json.output.Profile.contact.email || "Email not available",
      phone: $('Profile Analyzer Agent').first().json.output.Profile.contact.phone ||  "Phone not available",
      position: $('Profile Analyzer Agent').first().json.output.AppliedPosition,
      overall_fit_score: evalResult.overall_fit_score,
      recommendation: evalResult.recommendation,
      strengths: evalResult.strengths.join(', '),
      concerns_or_gaps: evalResult.concerns_or_gaps.join(', '),
      final_notes: evalResult.final_notes,
      submitted_date: $now.toFormat("yyyyLLdd-HHmmss")
    }
  }
];

