// This function checks the lead score and outputs it if >= 60
// If score is < 60, it outputs 60 as the threshold value
function checkLeadScore(items) {
  // Process each item coming into the node
  return items.map(item => {
    // Extract the lead score from the Airtable data structure
    // Based on your document, the score is in fields["Overall Score"]
    let leadScore;

    if (item.json.fields && item.json.fields["Overall Score"]) {
      leadScore = item.json.fields["Overall Score"];
    } else {
      // If we can't find the score in the expected location, check other possible places
      if (item.json.Overall_Score) {
        leadScore = item.json.Overall_Score;
      } else if (item.json.overallScore) {
        leadScore = item.json.overallScore;
      } else {
        // Default to threshold if we can't find any score
        leadScore = "60/100";
      }
    }

    // Try to parse the score to make sure it's a number
    let scoreValue;

    try {
      // Handle different possible formats
      if (typeof leadScore === 'string') {
        // If score is in format like "78/100"
        if (leadScore.includes('/')) {
          scoreValue = parseInt(leadScore.split('/')[0], 10);
        } else {
          scoreValue = parseInt(leadScore, 10);
        }
      } else if (typeof leadScore === 'number') {
        scoreValue = leadScore;
      } else {
        // Default to threshold if we can't parse
        scoreValue = 60;
      }
    } catch (error) {
      // If parsing fails, default to threshold
      scoreValue = 60;
    }

    // Check if score is less than threshold
    const result = (isNaN(scoreValue) || scoreValue < 60) ? 60 : scoreValue;

    // Add the result to the item
    item.json.scoreThreshold = result;

    // Log for debugging
    console.log(`Original Score: ${leadScore}, Parsed: ${scoreValue}, Result: ${result}`);

    return item;
  });
}

