function parseRssFeed(jsonData) {
  try {
    // Parse JSON input
    const dataObj = JSON.parse(jsonData);
    const xmlString = dataObj[0].json.data;

    // Simple regex-based XML parser for RSS items
    const itemRegex = /<item>([\s\S]*?)<\/item>/g;
    const items = [];
    let match;

    // Extract all <item> tags
    while ((match = itemRegex.exec(xmlString)) !== null) {
      const itemContent = match[1];
      const item = {};

      // Define fields to extract
      const fields = [
        { tag: 'title', key: 'title' },
        { tag: 'description', key: 'description' },
        { tag: 'link', key: 'link' },
        { tag: 'guid', key: 'guid' },
        { tag: 'pubDate', key: 'pubDate' },
        { tag: 'category', key: 'category' },
        { tag: 'dc:creator', key: 'dc_creator' },
        { tag: 'media:content', key: 'media_content', subtag: 'media:credit' },
        { tag: 'media:content', key: 'media_text', subtag: 'media:text' },
        { tag: 'media:content', key: 'media_title', subtag: 'media:title' },
        { tag: 'dc:content', key: 'content' }
      ];

      // Extract each field
      fields.forEach(field => {
        let regex;
        if (field.subtag) {
          regex = new RegExp(`<${field.tag}[^>]*>[\\s\\S]*?<${field.subtag}[^>]*><!\\[CDATA\\[(.*?)\\]\\]></${field.subtag}>`, 'i');
        } else {
          regex = new RegExp(`<${field.tag}(?:[^>]*)><!\\[CDATA\\[(.*?)\\]\\]></${field.tag}>|<${field.tag}(?:[^>]*)>(.*?)</${field.tag}>`, 'i');
        }

        const fieldMatch = itemContent.match(regex);
        if (fieldMatch) {
          let value = fieldMatch[1] || fieldMatch[2] || '';
          // Remove HTML tags
          value = value.replace(/<[^>]+>/g, '').replace(/&nbsp;/g, ' ').replace(/&amp;/g, '&').trim();
          if (value) {
            item[field.key] = value;
          }
        }
      });

      // Handle multiple categories
      const categoryMatches = itemContent.match(/<category><!\[CDATA\[(.*?)\]\]><\/category>/g);
      if (categoryMatches) {
        item.category = categoryMatches
          .map(cat => cat.match(/<category><!\[CDATA\[(.*?)\]\]><\/category>/)[1])
          .join(', ');
      }

      if (Object.keys(item).length > 0) {
        items.push(item);
      }
    }

    return items;
  } catch (error) {
    throw new Error(`Error processing RSS feed: ${error?.message}`);
  }
}

