const out = [];
for (const it of $input.all()) {
  const b64 = it.binary?.data?.data; // from Drive Download
  if (!b64) throw new Error('No binary "data". Use Download for txt/md');
  const text = Buffer.from(b64, 'base64').toString('utf8');
  const title = it.json?.name || it.json?.fileName || '회의록';

  out.push({
    json: { title, transcript: text },
    binary: it.binary // keep original file for Slack upload
  });
}
return out;

const it = $input.first();

// Pick the model output from any of the shapes n8n returns
const md =
  it.json?.message?.content ??                    // Message a model (simplified)
  it.json?.output_text ??                         // some builds expose this
  it.json?.data?.choices?.[0]?.message?.content ??// raw OpenAI response
  it.json?.choices?.[0]?.message?.content ??      // another raw variant
  '';

const title = it.json?.title || '회의록';
const filename = `${title}.md`;
const data = Buffer.from(md, 'utf8').toString('base64');

return [{
  json: { filename, title },
  binary: { minutes: { data, fileName: filename, mimeType: 'text/markdown' } }
}];
