/**
 * MarkdownV2-safe formatter + auto-chunker for Telegram (n8n Code node)
 * --------------------------------------------------------------------
 * - Allows: *bold*, _italic_, ||spoiler||, [label](url)
 * - Escapes everything else for Telegram MarkdownV2
 * - Validates/normalizes URLs
 * - Converts "# Heading" lines to bold titles
 * - Splits long messages into <= 4096-char chunks (uses a 4000-char budget)
 * - Outputs one item per chunk so the Telegram node sends all parts
 *
 * Recommended: Run this node in "Run Once for All Items".
 */

const MAX_TELEGRAM = 4096;
const SAFE_BUDGET = 4000; // small margin to avoid edge overflows

// ============ MarkdownV2 helpers ============
function escapeMarkdownV2(text) {
  if (!text) return '';
  return String(text).replace(/([\\_*[\]()~`>#+\-=|{}.!])/g, '\\$1');
}

function escapeForUrl(url) {
  return String(url).replace(/[)\\]/g, '\\$&');
}

function normalizeAndValidateUrl(url) {
  let raw = String(url || '').trim();
  try {
    const u = new URL(raw);
    return u.toString();
  } catch {}
  // Try https:// for bare domains
  const domainLike = /^[a-z0-9.-]+\.[a-z]{2,}([/:?#].*)?$/i.test(raw);
  if (domainLike) {
    try {
      const u2 = new URL('https://' + raw);
      return u2.toString();
    } catch {}
  }
  return null;
}

function normalizeHeadings(text) {
  // Turn "# Title" ‚Üí "*Title*"
  return text.replace(/^(#{1,6})\s+(.*)$/gm, (m, hashes, title) => `*${title.trim()}*`);
}

function normalizeCommonMd(text) {
  return String(text)
    .replace(/\*\*([\s\S]*?)\*\*/g, '*$1*') // **bold** ‚Üí *bold*
    .replace(/__([\s\S]*?)__/g, '_$1_');    // __italic__ ‚Üí _italic_
}

/**
 * Convert incoming text to Telegram-safe MarkdownV2.
 */
function processMarkdownV2Safe(inputText) {
  if (!inputText) return '';

  let text = normalizeCommonMd(String(inputText));
  text = normalizeHeadings(text);

  const placeholders = { links: [], bolds: [], italics: [], spoilers: [] };

  // Links: keep safe via placeholders during escaping
  text = text.replace(/\[([^\]\n]+)\]\(([^)]+)\)/g, (m, label, url) => {
    const normalizedUrl = normalizeAndValidateUrl(url);
    if (!normalizedUrl) return escapeMarkdownV2(label);
    const idx = placeholders.links.length;
    const ph = `‚ü¨L${idx}‚ü≠`;
    const safeLabel = escapeMarkdownV2(label);
    const safeUrl = escapeForUrl(normalizedUrl);
    placeholders.links.push(`[${safeLabel}](${safeUrl})`);
    return ph;
  });

  // Bold
  text = text.replace(/\*([\s\S]+?)\*/g, (m, inner) => {
    const idx = placeholders.bolds.length;
    const ph = `‚ü¨B${idx}‚ü≠`;
    placeholders.bolds.push(`*${escapeMarkdownV2(inner)}*`);
    return ph;
  });

  // Italic
  text = text.replace(/_([\s\S]+?)_/g, (m, inner) => {
    const idx = placeholders.italics.length;
    const ph = `‚ü¨I${idx}‚ü≠`;
    placeholders.italics.push(`_${escapeMarkdownV2(inner)}_`);
    return ph;
  });

  // Spoilers
  text = text.replace(/\|\|([\s\S]+?)\|\|/g, (m, inner) => {
    const idx = placeholders.spoilers.length;
    const ph = `‚ü¨S${idx}‚ü≠`;
    placeholders.spoilers.push(`||${escapeMarkdownV2(inner)}||`);
    return ph;
  });

  // Escape everything else
  text = escapeMarkdownV2(text);

  // Restore placeholders
  placeholders.links.forEach((md, i) => { text = text.replace(`‚ü¨L${i}‚ü≠`, md); });
  placeholders.bolds.forEach((md, i) => { text = text.replace(`‚ü¨B${i}‚ü≠`, md); });
  placeholders.italics.forEach((md, i) => { text = text.replace(`‚ü¨I${i}‚ü≠`, md); });
  placeholders.spoilers.forEach((md, i) => { text = text.replace(`‚ü¨S${i}‚ü≠`, md); });

  return text;
}

// ============ Chunking helpers ============
/**
 * Split text into Telegram-safe chunks <= maxLen.
 * Prefers paragraph boundaries, then sentence boundaries, then words.
 * Falls back to hard cuts only when unavoidable (e.g., extremely long URL).
 */
function chunkForTelegram(text, maxLen = SAFE_BUDGET) {
  if (!text || text.length <= maxLen) return [text || ''];

  const parts = [];
  let buffer = '';

  const flush = () => {
    if (buffer) {
      parts.push(buffer);
      buffer = '';
    }
  };

  // 1) Paragraph-level packing
  const paragraphs = text.split(/\n{2,}/);
  for (const pRaw of paragraphs) {
    const p = pRaw; // keep paragraph as-is
    const candidate = buffer ? buffer + '\n\n' + p : p;
    if (candidate.length <= maxLen) {
      buffer = candidate;
      continue;
    }
    if (p.length <= maxLen) {
      flush();
      buffer = p;
      continue;
    }

    // 2) Sentence-level packing (paragraph is still too big)
    flush();
    const sentences = p.split(/(?<=[.!?‚Ä¶])\s+(?=[^\s])/u);
    let sBuf = '';
    for (const s of sentences) {
      const sCandidate = sBuf ? sBuf + ' ' + s : s;
      if (sCandidate.length <= maxLen) {
        sBuf = sCandidate;
        continue;
      }
      if (s.length <= maxLen) {
        if (sBuf) parts.push(sBuf);
        sBuf = s;
        continue;
      }

      // 3) Word-level packing (sentence is still too big)
      if (sBuf) { parts.push(sBuf); sBuf = ''; }
      let wBuf = '';
      const words = s.split(/\s+/);
      for (const w of words) {
        const wCandidate = wBuf ? wBuf + ' ' + w : w;
        if (wCandidate.length <= maxLen) {
          wBuf = wCandidate;
          continue;
        }
        if (w.length <= maxLen) {
          if (wBuf) parts.push(wBuf);
          wBuf = w;
          continue;
        }
        // 4) Hard split (extremely long token, e.g., massive URL)
        if (wBuf) { parts.push(wBuf); wBuf = ''; }
        const re = new RegExp(`.{1,${maxLen}}`, 'g');
        const hardPieces = w.match(re) || [];
        parts.push(...hardPieces);
      }
      if (wBuf) parts.push(wBuf);
    }
    if (sBuf) parts.push(sBuf);
  }
  if (buffer) parts.push(buffer);

  // Final safety pass: trim chunks that might still exceed MAX_TELEGRAM
  return parts.flatMap(part => {
    if (part.length <= MAX_TELEGRAM) return [part];
    const re = new RegExp(`.{1,${SAFE_BUDGET}}`, 'g');
    return part.match(re) || [];
  });
}

// ============ Main ============
const inputItems = $input.all();
const out = [];

for (const item of inputItems) {
  const j = item.json || {};
  const raw =
    j.message ?? j.output ?? j.text ?? j.content ?? '';

  const formatted = processMarkdownV2Safe(raw);
  const chunks = chunkForTelegram(formatted, SAFE_BUDGET);

  chunks.forEach((chunk, idx) => {
    out.push({
      json: {
        ...j,
        message: chunk,
        message_part_index: idx + 1,
        message_parts_total: chunks.length,
      },
      binary: item.binary,
    });
  });
}

return out;

// --- Mapa Extendido de Tipos MIME ---
// Una lista completa para cubrir la mayor√≠a de los formatos de archivo comunes.
const mimeMap = {
  // --- Formatos de Documentos ---
  'pdf': 'application/pdf',
  'txt': 'text/plain',
  'rtf': 'application/rtf',
  'csv': 'text/csv',
  'html': 'text/html',
  'htm': 'text/html',
  'json': 'application/json',
  'xml': 'application/xml', // 'text/xml' tambi√©n es v√°lido pero 'application/xml' es m√°s com√∫n
  'yaml': 'application/x-yaml',
  'yml': 'application/x-yaml',

  // --- Formatos de Microsoft Office ---
  'doc': 'application/msword',
  'docx': 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
  'xls': 'application/vnd.ms-excel',
  'xlsx': 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
  'ppt': 'application/vnd.ms-powerpoint',
  'pptx': 'application/vnd.openxmlformats-officedocument.presentationml.presentation',
  'pub': 'application/vnd.ms-publisher',

  // --- Formatos de OpenOffice / LibreOffice ---
  'odt': 'application/vnd.oasis.opendocument.text',
  'ods': 'application/vnd.oasis.opendocument.spreadsheet',
  'odp': 'application/vnd.oasis.opendocument.presentation',
  'odg': 'application/vnd.oasis.opendocument.graphics',

  // --- Formatos de Apple iWork ---
  'pages': 'application/vnd.apple.pages',
  'numbers': 'application/vnd.apple.numbers',
  'key': 'application/vnd.apple.keynote',

  // --- Formatos de Imagen ---
  'png': 'image/png',
  'jpg': 'image/jpeg',
  'jpeg': 'image/jpeg',
  'gif': 'image/gif',
  'webp': 'image/webp',
  'svg': 'image/svg+xml',
  'bmp': 'image/bmp',
  'ico': 'image/vnd.microsoft.icon',
  'tif': 'image/tiff',
  'tiff': 'image/tiff',
  'heic': 'image/heic',
  'heif': 'image/heif',

  // --- Formatos de Audio ---
  'mp3': 'audio/mpeg',
  'wav': 'audio/wav',
  'oga': 'audio/ogg',
  'ogg': 'audio/ogg',
  'flac': 'audio/flac',
  'm4a': 'audio/mp4',
  'aac': 'audio/aac',
  'opus': 'audio/opus',
  'wma': 'audio/x-ms-wma',
  'mid': 'audio/midi',
  'midi': 'audio/midi',

  // --- Formatos de Video ---
  'mp4': 'video/mp4',
  'mov': 'video/quicktime',
  'webm': 'video/webm',
  'mpeg': 'video/mpeg',
  'mpg': 'video/mpeg',
  'avi': 'video/x-msvideo',
  'wmv': 'video/x-ms-wmv',
  'flv': 'video/x-flv',
  'mkv': 'video/x-matroska',

  // --- Formatos de Archivos y Compresi√≥n ---
  'zip': 'application/zip',
  'rar': 'application/vnd.rar',
  '7z': 'application/x-7z-compressed',
  'tar': 'application/x-tar',
  'gz': 'application/gzip',
  'bz2': 'application/x-bzip2',

  // --- Otros Formatos ---
  'epub': 'application/epub+zip',
  'ics': 'text/calendar',
  'vcf': 'text/vcard',
  'js': 'text/javascript',
  'css': 'text/css',
  'sh': 'application/x-sh',
  'py': 'text/x-python',
};

// --- L√≥gica de Procesamiento (sin cambios) ---

// Obtenemos todos los items que llegan al nodo
const items = $input.all();

// Iteramos sobre cada item para procesarlo
for (const item of items) {
  // Verificamos que el item tenga datos binarios para procesar
  if (item.binary && item.binary['data']) {
    // Obtenemos el nombre del archivo de forma segura
    const fileName = item.binary['data'].fileName || '';
    if (!fileName) {
      continue; // Si no hay nombre de archivo, pasamos al siguiente item
    }

    // Extraemos la extensi√≥n del archivo de forma robusta
    const extension = fileName.slice((fileName.lastIndexOf(".") - 1 >>> 0) + 2).toLowerCase();

    // Buscamos la extensi√≥n en nuestro mapa
    const newMimeType = mimeMap[extension];

    // Si encontramos una coincidencia en el mapa, actualizamos el mimeType
    if (newMimeType) {
      if(item.binary['data'].mimeType !== newMimeType) {
        console.log(`Cambiando mimeType para '${fileName}' de '${item.binary['data'].mimeType}' a '${newMimeType}'.`);
        item.binary['data'].mimeType = newMimeType;
      }
    }
  }
}

// Devolvemos todos los items, modificados o no
return items;

parameters.query:
CREATE TABLE IF NOT EXISTS public.chat_memory (
  id SERIAL PRIMARY KEY,
  session_id VARCHAR(255) NOT NULL,
  message TEXT DEFAULT 'Could not get data'
) TABLESPACE pg_default;

CREATE INDEX IF NOT EXISTS chat_memory_session_id_idx 
ON public.chat_memory USING btree (session_id) 
TABLESPACE pg_default;
