// Limit articles per domain for balanced coverage
const input = $input.all();
const grouped = {};

for (const item of input) {
  const link = item.json.link;
  const domainMatch = link.match(/\/\/([^\/]+)/);
  const domain = domainMatch ? domainMatch[1].replace(/^www\./, '') : 'unknown';

  if (!grouped[domain]) grouped[domain] = [];
  if (grouped[domain].length < 5) {
    grouped[domain].push(item.json);
  }
}

// Flatten results
const result = [];
for (const domain in grouped) {
  result.push(...grouped[domain]);
}

return result.map(i => ({ json: i }));
