// Example Code node
// Input: results from urlscan.io + VirusTotal + Telegram trigger

const items = $input.all();

// Loop through items so we don’t drop anything
return items.map(item => {
  const urlscan = item.json.urlscan || {};
  const virustotal = item.json.virustotal || {};

  let summary = "";

  if (urlscan.message) {
    summary = `✅ urlscan.io result:\n${JSON.stringify(urlscan)}\n\n✅ VirusTotal result:\n${JSON.stringify(virustotal)}`;
  } else {
    summary = `⚠️ urlscan.io scan failed. Falling back to VirusTotal only:\n${JSON.stringify(virustotal)}`;
  }

  // Preserve original fields (chatId, etc.)
  return {
    json: {
      ...item.json,
      summary, // add your summary field
    },
    binary: item.binary ?? undefined, // keep binary if exists
  };
});
