// –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –∏ –¥–µ–¥—É–ø–ª–∏–∫–∞—Ü–∏—è –Ω–æ–≤–æ—Å—Ç–µ–π
const items = $input.all();
const now = new Date();
const yesterday = new Date(now.getTime() - 24 * 60 * 60 * 1000);

// –£–±–∏—Ä–∞–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã –∏ —Ñ–∏–ª—å—Ç—Ä—É–µ–º –ø–æ –≤—Ä–µ–º–µ–Ω–∏
const uniqueNews = [];
const seenUrls = new Set();

for (const item of items) {
  const data = item.json;

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –¥–∞—Ç—É –ø—É–±–ª–∏–∫–∞—Ü–∏–∏
  const pubDate = new Date(data.pubDate || data.isoDate);
  if (pubDate < yesterday) continue;

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞ –¥—É–±–ª–∏–∫–∞—Ç—ã
  if (seenUrls.has(data.link)) continue;
  seenUrls.add(data.link);

  // –ë–µ—Ä–µ–º –≤—Å–µ –¥–æ—Å—Ç—É–ø–Ω—ã–µ –ø–æ–ª—è –∫–æ–Ω—Ç–µ–Ω—Ç–∞
  const rssContent = data['content:encodedSnippet'] || data.content || data.contentSnippet || data.summary || '';

  // –û—á–∏—â–∞–µ–º RSS –∫–æ–Ω—Ç–µ–Ω—Ç –æ—Ç HTML
  const cleanRssContent = rssContent
    .replace(/<[^>]*>/g, '')
    .replace(/&[^;]+;/g, '')
    .replace(/\s+/g, ' ')
    .trim();

  // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –æ–±—ä–µ–∫—Ç, —Ç–µ–ø–µ—Ä—å —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º contentSnippet
  uniqueNews.push({
    title: data.title,
    link: data.link,
    rssContent: cleanRssContent,
    contentSnippet: data.contentSnippet, // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—ã–π contentSnippet
    pubDate: data.pubDate,
    source: data['dc:creator'] || data.meta?.title || 'Unknown',
    guid: data.guid
  });
}

console.log(`Filtered ${uniqueNews.length} unique news from ${items.length} total`);

return uniqueNews.map(item => ({ json: item }));

// 1. –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ –≤—Ö–æ–¥—è—â–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã –≤ –≤–∏–¥–µ –º–∞—Å—Å–∏–≤–∞
const allItems = $input.all();

// 2. –ò–∑ –∫–∞–∂–¥–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞ –∑–∞–±–∏—Ä–∞–µ–º —Ç–æ–ª—å–∫–æ –µ–≥–æ 'json' —á–∞—Å—Ç—å (—Å–∞–º—É –Ω–æ–≤–æ—Å—Ç—å)
// –∏ —Å–∫–ª–∞–¥—ã–≤–∞–µ–º –≤ –Ω–æ–≤—ã–π –º–∞—Å—Å–∏–≤.
const newsArray = allItems.map(item => item.json);

// 3. –í–æ–∑–≤—Ä–∞—â–∞–µ–º –û–î–ò–ù-–ï–î–ò–ù–°–¢–í–ï–ù–ù–´–ô –Ω–æ–≤—ã–π —ç–ª–µ–º–µ–Ω—Ç.
// –£ —ç—Ç–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞ –±—É–¥–µ—Ç –ø–æ–ª–µ 'data', –∫–æ—Ç–æ—Ä–æ–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –º–∞—Å—Å–∏–≤ –≤—Å–µ—Ö –Ω–æ–≤–æ—Å—Ç–µ–π.
// –ò–º—è 'data' –≤–∞–∂–Ω–æ, —Ç–∞–∫ –∫–∞–∫ –æ–Ω–æ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è –≤ –ø—Ä–æ–º—Ç–µ: {{$json.data}}
return [{
  json: {
    data: newsArray
  }
}];

// –ü–æ–ª—É—á–∞–µ–º –¥–ª–∏–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ —É–∑–ª–∞ (AI-–∞–≥–µ–Ω—Ç–∞)
const longText = $json.output;

// –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –º–∞–∫—Å–∏–º–∞–ª—å–Ω—É—é –¥–ª–∏–Ω—É —Å–æ–æ–±—â–µ–Ω–∏—è –≤ Telegram
const maxLength = 4096;

// –ú–µ—Å—Ç–æ, –≥–¥–µ –º—ã –±—É–¥–µ–º —Ö—Ä–∞–Ω–∏—Ç—å –≥–æ—Ç–æ–≤—ã–µ –∫—É—Å–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏—è
const chunks = [];

let remainingText = longText;

// –ó–∞–ø—É—Å–∫–∞–µ–º —Ü–∏–∫–ª, –∫–æ—Ç–æ—Ä—ã–π –±—É–¥–µ—Ç —Ä–∞–±–æ—Ç–∞—Ç—å, –ø–æ–∫–∞ –Ω–µ "–Ω–∞—Ä–µ–∂–µ–º" –≤–µ—Å—å —Ç–µ–∫—Å—Ç
while (remainingText.length > 0) {
  // –ï—Å–ª–∏ –æ—Å—Ç–∞–≤—à–∏–π—Å—è —Ç–µ–∫—Å—Ç —É–∂–µ –ø–æ–º–µ—â–∞–µ—Ç—Å—è –≤ –æ–¥–Ω–æ —Å–æ–æ–±—â–µ–Ω–∏–µ, –ø—Ä–æ—Å—Ç–æ –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –∏ –≤—ã—Ö–æ–¥–∏–º
  if (remainingText.length <= maxLength) {
    chunks.push(remainingText);
    break;
  }

  // –ï—Å–ª–∏ —Ç–µ–∫—Å—Ç —Å–ª–∏—à–∫–æ–º –¥–ª–∏–Ω–Ω—ã–π, –∏—â–µ–º –ø–æ—Å–ª–µ–¥–Ω–µ–µ "–∫—Ä–∞—Å–∏–≤–æ–µ" –º–µ—Å—Ç–æ –¥–ª—è —Ä–∞–∑—Ä—ã–≤–∞
  // (–¥–≤–æ–π–Ω–æ–π –ø–µ—Ä–µ–Ω–æ—Å —Å—Ç—Ä–æ–∫–∏) –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö –ª–∏–º–∏—Ç–∞
  let splitAt = remainingText.lastIndexOf('\n\n', maxLength);

  // –ï—Å–ª–∏ –Ω–µ –Ω–∞—à–ª–∏ –¥–≤–æ–π–Ω–æ–≥–æ –ø–µ—Ä–µ–Ω–æ—Å–∞ (–Ω–∞–ø—Ä–∏–º–µ—Ä, –æ–¥–Ω–∞ –Ω–æ–≤–æ—Å—Ç—å —Å–∞–º–∞ –ø–æ —Å–µ–±–µ > 4096 —Å–∏–º–≤–æ–ª–æ–≤),
  // —Ç–æ –ø—Ä–æ—Å—Ç–æ –∏—â–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–π –ø–µ—Ä–µ–Ω–æ—Å —Å—Ç—Ä–æ–∫–∏
  if (splitAt === -1) {
    splitAt = remainingText.lastIndexOf('\n', maxLength);
  }

  // –ï—Å–ª–∏ –¥–∞–∂–µ –æ–±—ã—á–Ω–æ–≥–æ –ø–µ—Ä–µ–Ω–æ—Å–∞ –Ω–µ –Ω–∞—à–ª–∏, —Ä–µ–∂–µ–º "–ø–æ –∂–∏–≤–æ–º—É", —á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –±–µ—Å–∫–æ–Ω–µ—á–Ω–æ–≥–æ —Ü–∏–∫–ª–∞
  if (splitAt === -1) {
      splitAt = maxLength;
  }

  // "–û—Ç–∫—É—Å—ã–≤–∞–µ–º" –∫—É—Å–æ–∫ –∏ –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –≤ –Ω–∞—à –º–∞—Å—Å–∏–≤
  chunks.push(remainingText.substring(0, splitAt));

  // –û–±–Ω–æ–≤–ª—è–µ–º –æ—Å—Ç–∞–≤—à–∏–π—Å—è —Ç–µ–∫—Å—Ç
  remainingText = remainingText.substring(splitAt).trim();
}

// –ù–∞ –≤—ã—Ö–æ–¥–µ –º—ã –ø–æ–ª—É—á–∞–µ–º –º–∞—Å—Å–∏–≤ –∏–∑ –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤,
// –∫–∞–∂–¥—ã–π –∏–∑ –∫–æ—Ç–æ—Ä—ã—Ö —Å–æ–¥–µ—Ä–∂–∏—Ç –∫—É—Å–æ–∫ —Ç–µ–∫—Å—Ç–∞ –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏.
// –ú—ã –¥–æ–ª–∂–Ω—ã –≤–µ—Ä–Ω—É—Ç—å –∏—Ö –≤ —Ñ–æ—Ä–º–∞—Ç–µ, –∫–æ—Ç–æ—Ä—ã–π n8n —Å–º–æ–∂–µ—Ç –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å –ø–æ –æ–¥–Ω–æ–º—É.
return chunks.map(chunk => ({
  json: {
    // –£–∑–µ–ª Telegram –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –∏—â–µ—Ç —Ç–µ–∫—Å—Ç –≤ –ø–æ–ª–µ 'text'.
    // –ú—ã —Å—Ä–∞–∑—É –∫–ª–∞–¥–µ–º –Ω–∞—à –∫—É—Å–æ–∫ –≤ —ç—Ç–æ –ø–æ–ª–µ.
    text: chunk
  }
}));
