// Get the input string
let content = $input.first().json.output;

// Remove all newlines
content = content.replace(/\n/g, '');

// Remove em dashes
content = content.replace(/‚Äî/g, ' ');

// Extract the content inside the first <h1> tag
const h1Match = content.match(/<h1[^>]*>(.*?)<\/h1>/);
const h1Content = h1Match ? h1Match[1] : '';

// Remove all <h1> tags and their content from the original string
const excludedH1Content = content.replace(/<h1[^>]*>.*?<\/h1>/g, '');

return {
  json: {
    h1Only: h1Content,
    excludedH1: excludedH1Content
  }
};
