// Input: Pass your JSON into this Code node
// Example: {{ $json }}

const input = $json.output.response.output;

// Helper function to clean and normalize text
function cleanText(text) {
  return text
    .replace(/^#+\s.*$/gm, "")       // remove markdown headings like ### Blog Content
    .replace(/\*\*[^*]+\*\*:/g, "")  // remove bold section headers e.g. **Title:**
    .replace(/\*{2}/g, "")           // remove leftover bold markers
    .replace(/\n{2,}/g, "\n\n")      // normalize multiple line breaks
    .replace(/\s+$/g, "")            // trim trailing spaces
    .trim();
}

// Extract and clean fields
const title = cleanText(input.title || "");
const article = cleanText(input.Article || "");
const category = cleanText(input.category || "");

// Return as separate fields in n8n
return [
  {
    json: {
      title,
      article,
      category
    }
  }
];
