// Get the input text from the previous node
const input = $json.data || $json.text || "";

// Remove "Page X" markers
let text = input.replace(/Page\s*\d+/gi, "");

// Remove all occurrences of double newlines
text = text.replace(/\n\n+/g, " ");

// Remove all occurrences of single newlines
text = text.replace(/\n+/g, " ");

// Step 5: Trim
text = text.trim();

// Output for next node
return { json: { normalizedText: text } };
