// Robust SEO analyzer:
// - Accepts html from item.json.body OR item.json.data OR item.binary.data (base64)
// - Guesses siteUrl from canonical/og:url/base if missing (enhanced with JSON-LD, home/logo, dominant domain, form/asset hosts, first absolute URL)
// - Falls back to og:description if meta[name="description"] missing
// - Returns: url, seo{}, summary{}, issues[], brokenLinks[], critical, plus config passthrough

const out = [];

function getHtmlFromItem(item) {
  // Prefer explicit fields first
  if (typeof item.json.body === 'string' && item.json.body.trim()) return { html: item.json.body, source: 'json.body' };
  if (typeof item.json.data === 'string' && item.json.data.trim()) return { html: item.json.data, source: 'json.data' };

  // Binary (e.g., from HTTP node set to "File")
  if (item.binary && item.binary.data && item.binary.data.data) {
    try {
      const decoded = Buffer.from(item.binary.data.data, 'base64').toString('utf8');
      if (decoded && /<html[^>]*>/i.test(decoded)) return { html: decoded, source: 'binary.data' };
    } catch {}
  }
  return { html: '', source: 'none' };
}

for (const item of items) {
  const { html, source: htmlSource } = getHtmlFromItem(item);

  // Pull config if present (still works with your Set Config + Merge approach)
  const timeoutMs = Number(item.json.timeoutMs ?? 5000);
  const maxLinkChecks = Number(item.json.maxLinkChecks ?? 30);

  // helpers
  const issues = [];
  const warn = (msg) => issues.push({ level: "warning", msg });
  const crit = (msg) => issues.push({ level: "critical", msg });

  const grab = (re, s) => {
    const m = re.exec(s);
    return m ? m[1] : null;
  };
  const grabAll = (re, s) => {
    const arr = [];
    let m;
    while ((m = re.exec(s)) !== null) arr.push(m[1]);
    return arr;
  };

  // --- ADDED HELPERS (safe additions) ---
  const ABS_RE = /^https?:\/\//i;
  const isAbs = (u) => ABS_RE.test(u);
  const toAbs = (maybeUrl, base) => { try { return new URL(maybeUrl, base).href; } catch { return null; } };
  const originOf = (u) => { try { const x = new URL(u); return `${x.protocol}//${x.host}/`; } catch { return null; } };
  const hostOf   = (u) => { try { return new URL(u).host; } catch { return null; } };

  // Third-party patterns to ignore when inferring the site's own host
  const THIRD_PARTY = /(googleapis\.com|gstatic\.com|googletagmanager\.com|google-analytics\.com|doubleclick\.net|facebook\.com|twitter\.com|instagram\.com|youtube\.com|vimeo\.com|cloudfront\.net|fonts\.googleapis\.com|fonts\.gstatic\.com|ajax\.googleapis\.com)/i;

  // Parse JSON-LD to find a plausible page URL
  function jsonLdUrl(htmlText) {
    const blocks = [];
    let m;
    const re = /<script[^>]*type=["']application\/ld\+json["'][^>]*>([\s\S]*?)<\/script>/gi;
    while ((m = re.exec(htmlText)) !== null) blocks.push(m[1]);
    for (const raw of blocks) {
      try {
        const data = JSON.parse(raw.trim());
        const nodes = Array.isArray(data['@graph']) ? data['@graph'] : [data];
        for (const node of nodes) {
          if (node['@type'] && /WebPage/i.test(node['@type']) && typeof node.url === 'string' && node.url) return node.url;
          if (node.mainEntityOfPage) {
            if (typeof node.mainEntityOfPage === 'string') return node.mainEntityOfPage;
            if (typeof node.mainEntityOfPage === 'object' && typeof node.mainEntityOfPage['@id'] === 'string') return node.mainEntityOfPage['@id'];
          }
          if (typeof node.url === 'string' && node.url) return node.url;
        }
      } catch {}
    }
    return null;
  }

  // Try to find a home-like URL (rel=home / logo link)
  function homeLikeUrl(htmlText) {
    const relHome = grab(/<link[^>]*rel=["']home["'][^>]*href=["']([^"']+)["'][^>]*>/i, htmlText);
    if (relHome) return relHome;
    const aRelHome = grab(/<a[^>]*rel=["']home["'][^>]*href=["']([^"']+)["'][^>]*>/i, htmlText);
    if (aRelHome) return aRelHome;
    const aLogo = grab(/<a[^>]*class=["'][^"']*\blogo\b[^"']*["'][^>]*href=["']([^"']+)["'][^>]*>/i, htmlText)
               || grab(/<a[^>]*aria-label=["'][^"']*home[^"']*["'][^>]*href=["']([^"']+)["'][^>]*>/i, htmlText);
    return aLogo || null;
  }

  // Compute a dominant first-party domain by counting absolute href/src/action attributes (skip common CDNs)
  function dominantDomain(htmlText) {
    const attrRe = /\b(?:href|src|action)\s*=\s*["']([^"']+)["']/gi;
    const counts = new Map();
    let m;
    while ((m = attrRe.exec(htmlText)) !== null) {
      const u = (m[1] || '').trim();
      if (!u || !isAbs(u) || THIRD_PARTY.test(u)) continue;
      try {
        const h = new URL(u).host;
        counts.set(h, (counts.get(h) || 0) + 1);
      } catch {}
    }
    let best = null, n = 0;
    for (const [h, c] of counts.entries()) if (c > n) { best = h; n = c; }
    return best ? `https://${best}/` : null;
  }

  // Extra fallback: prefer form action or stylesheet/script host
  function firstPartyByFormOrAsset(htmlText) {
    // <form action="...">
    const formAction = grab(/<form\b[^>]*action=["']([^"']+)["']/i, htmlText);
    if (formAction && isAbs(formAction) && !THIRD_PARTY.test(formAction)) {
      const o = originOf(formAction);
      if (o) return o;
    }
    // Stylesheets and scripts that are absolute and not third-party
    const assets = grabAll(/\b(?:href|src)\s*=\s*["']([^"']+)["']/gi, htmlText)
      .filter(u => isAbs(u) && !THIRD_PARTY.test(u));
    for (const u of assets) {
      const o = originOf(u);
      if (o) return o;
    }
    return null;
  }

  // If siteUrl wasn't injected by Set Config or upstream merge, try to infer it
  let siteUrl = item.json.siteUrl || null;
  const canonical = grab(/<link[^>]*rel=["']canonical["'][^>]*href=["']([^"']+)["'][^>]*>/i, html);
  const ogUrl = grab(/<meta[^>]*property=["']og:url["'][^>]*content=["']([^"']+)["'][^>]*>/i, html);
  const baseHref = grab(/<base[^>]*href=["']([^"']+)["'][^>]*>/i, html);

  if (!siteUrl) {
    siteUrl = canonical || ogUrl || baseHref || "https://example.com";
  }

  // --- ENHANCEMENT: resolve relatives + alternate signals + normalize ---
  const domainByVoting = dominantDomain(html);
  const bestBase = baseHref || domainByVoting || null;

  const candidateIsPlaceholder = siteUrl === "https://example.com";
  const candidateIsRelative = !!siteUrl && !isAbs(siteUrl);

  if (candidateIsPlaceholder || candidateIsRelative) {
    // Prefer canonical (resolved), then og:url (resolved)
    let resolved = null;
    if (canonical) resolved = isAbs(canonical) ? canonical : (bestBase ? toAbs(canonical, bestBase) : null);
    if (!resolved && ogUrl) resolved = isAbs(ogUrl) ? ogUrl : (bestBase ? toAbs(ogUrl, bestBase) : null);

    // JSON-LD candidates (WebPage.url / mainEntityOfPage / url)
    const ld = jsonLdUrl(html);
    if (!resolved && ld) resolved = isAbs(ld) ? ld : (bestBase ? toAbs(ld, bestBase) : null);

    // Home/logo links as a hint
    const home = homeLikeUrl(html);
    if (!resolved && home) resolved = isAbs(home) ? home : (bestBase ? toAbs(home, bestBase) : null);

    // Fall back to dominant domain origin
    if (!resolved && domainByVoting) resolved = domainByVoting;

    // NEW: prefer form action / asset host
    if (!resolved) {
      const formOrAsset = firstPartyByFormOrAsset(html);
      if (formOrAsset) resolved = formOrAsset;
    }

    // NEW: last-resort — first absolute non-third-party URL's origin
    if (!resolved) {
      const firstAbs = grab(/\b(?:href|src|action)\s*=\s*["'](https?:\/\/[^"']+)["']/i, html);
      if (firstAbs && !THIRD_PARTY.test(firstAbs)) {
        const o = originOf(firstAbs);
        if (o) resolved = o;
      }
    }

    // Last resort: keep what we had
    siteUrl = resolved || siteUrl;
  }

  // Normalize to site origin (scheme + host + trailing slash)
  const normalizedOrigin = originOf(siteUrl);
  if (normalizedOrigin) siteUrl = normalizedOrigin;
  if (!/\/$/.test(siteUrl)) siteUrl = siteUrl + '/';

  // basic SEO fields
  const title = grab(/<title[^>]*>([\s\S]{0,2000}?)<\/title>/i, html)?.trim() || null;

  // Prefer meta[name="description"]; fall back to og:description if missing
  let metaDesc =
    grab(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']{0,4000})["'][^>]*>/i, html) ||
    grab(/<meta[^>]*property=["']og:description["'][^>]*content=["']([^"']{0,4000})["'][^>]*>/i, html) ||
    null;

  const robots = grab(/<meta[^>]*name=["']robots["'][^>]*content=["']([^"']+)["'][^>]*>/i, html);
  const h1s = grabAll(/<h1\b[^>]*>([\s\S]*?)<\/h1>/gi, html);

  // checks
  if (!title) crit("Missing <title> tag.");
  if (!metaDesc) warn("Missing meta description.");
  if (metaDesc && (metaDesc.length < 50 || metaDesc.length > 160)) {
    warn(`Meta description suboptimal length (${metaDesc.length}).`);
  }
  if (h1s.length === 0) warn("Missing <h1>.");
  if (h1s.length > 1) warn(`Multiple <h1> tags (${h1s.length}).`);
  if (!canonical) warn("Missing canonical link.");
  if (robots && /noindex/i.test(robots)) crit('robots meta includes "noindex".');
  if (robots && /nofollow/i.test(robots)) warn('robots meta includes "nofollow".');

  // images without alt
  const imgTags = html.match(/<img\b[^>]*>/gi) || [];
  let imagesMissingAlt = 0;
  for (const img of imgTags) {
    const hasAlt = /alt\s*=\s*["'][^"']*["']/i.test(img);
    if (!hasAlt) imagesMissingAlt++;
  }
  if (imagesMissingAlt > 0) warn(`${imagesMissingAlt} image(s) without alt.`);

  // internal link checking (HEAD, fallback GET)
  let host = null;
  try { host = new URL(siteUrl).host; } catch {}

  const linkRe = /<a\b[^>]*href=["']([^"']+)["'][^>]*>/gi;
  const linkHrefs = [];
  let m;
  while ((m = linkRe.exec(html)) !== null && linkHrefs.length < maxLinkChecks) {
    let href = m[1].trim();
    if (!href || /^javascript:|^mailto:|^tel:/i.test(href)) continue;
    try { href = new URL(href, siteUrl).href; } catch { continue; }
    try {
      const u = new URL(href);
      if (!host || u.host === host) linkHrefs.push(u.href);
    } catch {}
  }

  const uniqueLinks = [...new Set(linkHrefs)];
  const brokenLinks = [];

  async function checkUrl(url) {
    // Prefer HEAD
    try {
      const res = await this.helpers.httpRequest({
        method: 'HEAD',
        url,
        timeout: timeoutMs,
        resolveWithFullResponse: true,
        simple: false,
      });
      const code = res.statusCode || res.status || 0;
      if (code >= 400) brokenLinks.push({ url, status: code });
      return;
    } catch (e) {
      // fall back to GET
    }
    try {
      const res2 = await this.helpers.httpRequest({
        method: 'GET',
        url,
        timeout: timeoutMs,
        resolveWithFullResponse: true,
        simple: false,
      });
      const code2 = res2.statusCode || res2.status || 0;
      if (code2 >= 400) brokenLinks.push({ url, status: code2 });
    } catch (e2) {
      brokenLinks.push({ url, status: 'timeout/error' });
    }
  }

  // Sequential (gentle). Raise concurrency if you control the target.
  for (const u of uniqueLinks) {
    /* eslint-disable no-await-in-loop */
    await checkUrl.call(this, u);
  }

  if (brokenLinks.length > 0) crit(`${brokenLinks.length} broken internal link(s).`);

  const summary = {
    url: siteUrl,
    titlePresent: !!title,
    hasMetaDescription: !!metaDesc,
    metaDescriptionLength: metaDesc ? metaDesc.length : 0,
    h1Count: h1s.length,
    hasCanonical: !!canonical,
    robots: robots || null,
    imagesMissingAlt,
    internalLinksChecked: uniqueLinks.length,
    brokenLinksCount: brokenLinks.length,
    htmlSource
  };

  const critical = issues.some(i => i.level === "critical");

  out.push({
    json: {
      url: siteUrl,
      seo: {
        title,
        metaDesc,
        robots: robots || null,
        canonical: canonical || null,
        h1Count: h1s.length,
      },
      summary,
      brokenLinks,
      issues,
      critical,
      // pass through any config if present
      driveFolderId: item.json.driveFolderId,
      slackChannel: item.json.slackChannel,
      psiStrategy: item.json.psiStrategy || "mobile",
    }
  });
}

return out;

// // Node name: PSI (with backoff)
