// Node name: PSI (with backoff)
// Requires: env PSI_API_KEY (no default)
// Reads site URL from: item.json.url || item.json.summary?.url || item.json.siteUrl
// Outputs: item.json.psi = { ok, performance, rawError, testedUrl, fetchedAt, strategy }

const KEY = 'AIzaSyDb-9zvazRVz2w105NPec89NSTKN9-Ratg';
if (!KEY) {
  // Fail fast so you don't waste retries without a key.
  throw new Error('PSI_API_KEY is not set. Add it to n8n environment and restart.');
}

const ENDPOINT = 'https://pagespeedonline.googleapis.com/pagespeedonline/v5/runPagespeed';

async function sleep(ms) { return new Promise(r => setTimeout(r, ms)); }

function extractError(e) {
  // Try to normalize different error shapes from n8n/http/got
  if (!e) return 'unknown_error';
  if (typeof e === 'string') return e;
  if (e?.message) return e.message;
  const res = e?.response;
  if (res) {
    const status = res.statusCode || res.status;
    let body = res.body || res.data;
    if (Buffer.isBuffer(body)) body = body.toString('utf8');
    let msg = '';
    try {
      const j = typeof body === 'string' ? JSON.parse(body) : body;
      msg = j?.error?.message || j?.message || '';
    } catch {
      // not JSON
      if (typeof body === 'string') msg = body.slice(0, 200);
    }
    return `HTTP ${status}${msg ? `: ${msg}` : ''}`;
  }
  return 'request_failed';
}

async function runPSI(url, strategy, quotaUser, attempts = 6) {
  let lastErr = null;
  for (let i = 0; i < attempts; i++) {
    try {
      const res = await this.helpers.httpRequest({
        method: 'GET',
        url: ENDPOINT,
        qs: {
          url,
          strategy: strategy || 'mobile',
          category: 'performance',
          key: KEY,
          quotaUser: quotaUser || url, // helps distribute quota
        },
        json: true,
        // The helper ignores some libs' flags; we still guard with try/catch.
        // These hints help in newer n8n versions:
        resolveWithFullResponse: false,
        simple: false,
        throwHttpErrors: false,
        ignoreHttpStatusErrors: true,
      });

      // If PSI returned a valid JSON with lighthouseResult, we're done.
      if (res?.lighthouseResult?.categories?.performance?.score != null) {
        return res;
      }

      // If PSI returned JSON error shape, prepare to retry.
      if (res?.error?.message) {
        lastErr = new Error(res.error.message);
      } else if (typeof res === 'string' && /automated queries/i.test(res)) {
        // Google "Sorry" HTML
        lastErr = new Error('blocked_by_google_sorry_page');
      } else {
        // Unknown shape; stringify to inspect
        lastErr = new Error('psi_unknown_response');
      }
    } catch (e) {
      // Network or non-2xx thrown by transport
      const msg = extractError(e);
      lastErr = new Error(msg);
      // If it wasn't a 429-like situation, you can break early.
      if (!/429|rate|quota|limit|blocked/i.test(msg)) break;
    }

    // Backoff: 1s, 2s, 4s, 8s, 16s, 32s (+ jitter)
    const waitMs = (2 ** i) * 1000 + Math.floor(Math.random() * 400);
    await sleep(waitMs);
  }
  throw lastErr || new Error('psi_failed');
}

const out = [];
for (const item of items) {
  const siteUrl = item.json.url || item.json.summary?.url || item.json.siteUrl || 'https://example.com';
  const strategy = item.json.psiStrategy || 'mobile';

  const hostForQuota =
    (() => { try { return new URL(siteUrl).host; } catch { return siteUrl; } })();

  const psi = { ok: false, performance: null, rawError: null, testedUrl: null, fetchedAt: null, strategy: null };

  try {
    const raw = await runPSI.call(this, siteUrl, strategy, hostForQuota);
    const score = raw?.lighthouseResult?.categories?.performance?.score;
    psi.performance = (typeof score === 'number') ? Math.round(score * 100) : null;
    psi.ok = psi.performance !== null;
    psi.testedUrl = raw?.id || null;
    psi.fetchedAt = raw?.analysisUTCTimestamp || null;
    psi.strategy = raw?.lighthouseResult?.configSettings?.formFactor || strategy;
  } catch (e) {
    psi.rawError = extractError(e);
  }

  out.push({ json: { ...item.json, psi } });
}

return out;
