// Normalize Calendly booking data
const webhookData = $input.first().json;
const payload = webhookData.payload || webhookData;

// Handle different Calendly webhook structures
const event = payload.event || payload;
const invitee = event.invitee || event;
const eventType = event.event_type || event.event || {};

// Extract meeting details
const startTime = new Date(event.start_time || event.scheduled_event?.start_time);
const endTime = new Date(event.end_time || event.scheduled_event?.end_time);

// Parse custom form questions/answers
const questions = invitee.questions_and_responses || invitee.questions_and_answers || [];
let phone = '';
let notes = '';

// Extract phone and notes from questions
questions.forEach(qa => {
  const question = (qa.question || '').toLowerCase();
  if (question.includes('phone') || question.includes('mobile')) {
    phone = qa.answer || qa.response || '';
  } else if (question.includes('note') || question.includes('comment') || question.includes('message')) {
    notes += (qa.answer || qa.response || '') + ' ';
  }
});

const normalizedData = {
  name: invitee.name || 'Unknown',
  email: invitee.email || '',
  phone: phone.trim(),
  event_type: eventType.name || eventType.type || 'Meeting',
  date: startTime.toLocaleDateString(),
  time: `${startTime.toLocaleTimeString()} - ${endTime.toLocaleTimeString()}`,
  status: invitee.status || event.status || 'Scheduled',
  meeting_link: event.location?.join_url || event.join_url || '',
  notes: notes.trim(),
  duration: Math.round((endTime - startTime) / (1000 * 60)), // Duration in minutes
  timezone: invitee.timezone || event.timezone || 'UTC',
  booking_created: new Date().toISOString(),
  calendly_event_id: event.uuid || event.id || '',
  invitee_id: invitee.uuid || invitee.id || ''
};

console.log('Normalized Calendly booking:', {
  name: normalizedData.name,
  email: normalizedData.email,
  event_type: normalizedData.event_type,
  date: normalizedData.date,
  time: normalizedData.time
});

return {
  json: normalizedData
};

