// Supondo que o input é uma lista de mensagens no formato N8N (items)
const items = $input.all();

// Função para converter '25/07/2025' em um Date JS
function parseDate(dateStr) {
  const [day, month, year] = dateStr.split('/');
  return new Date(`${year}-${month}-${day}`);
}

// Função para obter o número da semana (ISO 8601)
function getWeek(date) {
  const tempDate = new Date(date.getTime());
  tempDate.setHours(0, 0, 0, 0);
  tempDate.setDate(tempDate.getDate() + 4 - (tempDate.getDay() || 7));
  const yearStart = new Date(tempDate.getFullYear(), 0, 1);
  const weekNo = Math.ceil((((tempDate - yearStart) / 86400000) + 1) / 7);
  return `${tempDate.getFullYear()}-W${weekNo}`;
}

// Agrupar mensagens por semana
const weeks = {};

for (const item of items) {
  const dataStr = item.json.Data;
  if (!dataStr) continue;
  const date = parseDate(dataStr);
  const week = getWeek(date);
  if (!weeks[week]) weeks[week] = [];
  weeks[week].push(item.json.mensagem);
}

// Compilar blocos por semana
const result = Object.entries(weeks).map(([week, mensagens]) => {
  return {
    json: {
      semana: week,
      bloco: mensagens.join('\n')
    }
  };
});

return result;
