const items = $input.all().map(i => i.json);

const result = {
  emails: [],
  name: [],
  subjects: []
};

for (const row of items) {
  if (row.Email && row.Email.trim() !== "") {
    result.emails.push(row.Email);
  }
  if (row.Name && row.Name.trim() !== "") {
    result.name.push(row.Name);
  }
  if (row.Subject && row.Subject.trim() !== "") {
    result.subjects.push(row.Subject);
  }
}

// Return as array of one item
return [{ json: result }];

const emails = $input.first().json.emails;   // list of blocked emails
const subjects = $input.first().json.subjects;
const names = $input.first().json.name;// list of blocked subjects

return $('Get weekly unread emails').all().filter(item => {
  const email = item.json.from.value[0].address;  
  const subject = item.json.subject;  
  const name = item.json.from.value[0].name; 

  return emails.includes(email) || subjects.includes(subject) ||names.includes(name) ;
});

// Get all input items
let items = $input.all();

// Initialize Slack blocks
let blocks = [];

for (let item of items) {
  let raw = item.json.output; // raw string with ```json ... ```

  // Clean backticks and parse
  let cleaned = raw.replace(/```json\n?/, "").replace(/```$/, "");
  let parsed = JSON.parse(cleaned);

  // Add Slack blocks
  blocks.push({ type: "divider" });
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*${parsed.from}*\n*${parsed.subject}*\n${parsed.summary}`
    }
  });
}

// Return one Slack message with all blocks
return [
  {
    json: {
      blocks: blocks
    }
  }
];

// Get all input items
let items = $input.all();

// Initialize Slack blocks
let blocks = [];

for (let item of items) {
  let text = item.json.output;

  blocks.push({ type: "divider" });
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `${text}`
    }
  });
}

return [
  {
    json: {
      blocks: blocks
    }
  }
];

const allInputs = $input.all();

const mergedBlocks = allInputs.flatMap(item => item.json.blocks || []);

return {
  json: {
    blocks: mergedBlocks
  }
};
