// Extract JSON string from $json.output (remove ```json ... ```)
let raw = $json.output.replace(/```json|```/g, "").trim();

// Parse JSON
let data;
try {
  data = JSON.parse(raw);
} catch (e) {
  throw new Error("Invalid JSON format in output: " + e.message);
}

// Slack blocks array
const blocks = [
  {
    type: "header",
    text: {
      type: "plain_text",
      text: "ðŸ“Š Customer Feedback Report",
      emoji: true
    }
  }
];

// Overall Sentiment
if (data.overallSentiment) {
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: `*Overall Sentiment:* ${data.overallSentiment}` }
  });
}

// Average Rating
if (data.averageRating) {
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: `*Average Rating:* ${data.averageRating} â­` }
  });
}

// Highlights
if (data.highlights && data.highlights.length > 0) {
  blocks.push({ type: "divider" });
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "*Highlights:*" }
  });

  data.highlights.forEach(h => {
    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: `â€¢ ${h}` }
    });
  });
}

// Weaknesses/Concerns
if (data.weaknessesConcerns && data.weaknessesConcerns.length > 0) {
  blocks.push({ type: "divider" });
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "*Weaknesses / Concerns:*" }
  });

  data.weaknessesConcerns.forEach(w => {
    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: `â€¢ ${w}` }
    });
  });
}

// Time Trend
if (data.timeTrend) {
  blocks.push({ type: "divider" });
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: `*Time Trend:* ${data.timeTrend}` }
  });
}

// Final Summary
if (data.finalSummary) {
  blocks.push({ type: "divider" });
  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: `*Final Summary:* ${data.finalSummary}` }
  });
}

// Return Slack-ready JSON
return [
  {
    json: {
      blocks
    }
  }
];

