const items = $input.all();
const today = new Date();
const oneWeekAgo = new Date();
oneWeekAgo.setDate(today.getDate() - 7);

const filteredItems = items.filter((item) => {
  const itemDate = new Date(item?.json?.formattedDate);
  return itemDate >= oneWeekAgo && itemDate <= today;
});

return filteredItems;

// Input: array of note-taker outputs
const notes = $input.all().map(item => item.json);

// Slack blocks array
let blocks = [];

notes.forEach((note, index) => {
  // Clean and parse JSON inside code block
  const cleaned = note.output.replace(/```json|```/g, '').trim();
  let parsed;

  try {
    parsed = JSON.parse(cleaned);
  } catch (err) {
    parsed = { title: `Meeting ${index + 1}`, summary: ["Could not parse notes"], action_items: [] };
  }

  // Meeting header with title
  blocks.push({
    type: "header",
    text: {
      type: "plain_text",
      text: parsed.title || `Meeting ${index + 1}`
    }
  });

  // Summary section
  if (parsed.summary && parsed.summary.length > 0) {
    blocks.push({
      type: "section",
      text: {
        type: "mrkdwn",
        text: "*Summary:*\n" + parsed.summary.map(s => `‚Ä¢ ${s}`).join("\n")
      }
    });
  }

  // Action items section
  if (parsed.action_items && parsed.action_items.length > 0) {
    blocks.push({
      type: "section",
      text: {
        type: "mrkdwn",
        text: "*Action Items:*"
      }
    });

    parsed.action_items.forEach(item => {
      blocks.push({
        type: "section",
        text: {
          type: "mrkdwn",
          text: `‚Ä¢ *Task:* ${item.task || "N/A"}\n   *Owner:* ${item.owner || "Unassigned"}\n   *Deadline:* ${item.deadline || "No deadline"}`
        }
      });
    });
  } else {
    // If no action items
    blocks.push({
      type: "section",
      text: {
        type: "mrkdwn",
        text: "_No action items available_"
      }
    });
  }

  // Divider
  blocks.push({ type: "divider" });
});

return { blocks };
