// Input:  $input.first().json.results[0].content
// Output: clean plain text (no HTML/JS/CSS, minimal \n)

function stripAll(html) {
  if (typeof html !== 'string') return '';

  // Remove scripts, styles, head, comments, svg, noscript, canvas
  html = html.replace(/<script[\s\S]*?<\/script>/gi, '');
  html = html.replace(/<style[\s\S]*?<\/style>/gi, '');
  html = html.replace(/<head[\s\S]*?<\/head>/gi, '');
  html = html.replace(/<noscript[\s\S]*?<\/noscript>/gi, '');
  html = html.replace(/<svg[\s\S]*?<\/svg>/gi, '');
  html = html.replace(/<canvas[\s\S]*?<\/canvas>/gi, '');
  html = html.replace(/<!--[\s\S]*?-->/g, '');

  // Replace block-level tags with a single newline
  const blockTags = [
    'p','div','section','article','header','footer','nav','aside','main',
    'h1','h2','h3','h4','h5','h6','ul','ol','li','table','tr','td','th','br','hr'
  ];
  for (const tag of blockTags) {
    const rxOpen  = new RegExp(`<${tag}[^>]*>`, 'gi');
    const rxClose = new RegExp(`</${tag}>`, 'gi');
    html = html.replace(rxOpen, '\n');
    html = html.replace(rxClose, '\n');
  }

  // Strip all remaining tags
  let text = html.replace(/<\/?[^>]+>/g, '');

  // Decode common HTML entities
  text = text
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'");

  // Clean whitespace
  text = text
    .replace(/\r/g, '')
    .replace(/[ \t]+/g, ' ')       // collapse spaces/tabs
    .replace(/\n[ \t]+/g,'')    // trim spaces after newlines
    .replace(/\n{3,}/g, '')    // collapse 3+ newlines into 2
    .trim();

  return text;
}

// MAIN
const html = $input.first().json?.results?.[0]?.content || '';
if (!html) {
  return [{ json: { error: 'No HTML found at json.results[0].content' } }];
}

const text = stripAll(html);

return [{
  json: {
    text,
    chars: text.length
  }
}];

