// Input shape expected:
// items[0].json.output = [ { title, author, rank, category, sub_category, rating, ratings_count, price:{currency, amount, format}, ... }, ... ]

function median(nums) {
  if (!nums.length) return 0;
  const arr = [...nums].sort((a, b) => a - b);
  const mid = Math.floor(arr.length / 2);
  return arr.length % 2 ? arr[mid] : (arr[mid - 1] + arr[mid]) / 2;
}

function sum(nums) {
  return nums.reduce((a, b) => a + (Number.isFinite(b) ? b : 0), 0);
}

function mean(nums) {
  const valid = nums.filter(n => Number.isFinite(n));
  return valid.length ? sum(valid) / valid.length : 0;
}

function fmtMoney(n, currency = "USD") {
  if (!Number.isFinite(n)) n = 0;
  try {
    return new Intl.NumberFormat("en-US", { style: "currency", currency }).format(n);
  } catch {
    return `$${n.toFixed(2)}`;
  }
}

function pad(str, len) {
  return (str + '').padEnd(len, ' ');
}

function repeat(s, n) {
  return Array.from({ length: n }, () => s).join('');
}

function safe(v, d='') { return (v === null || v === undefined) ? d : v; }

function asBar(value, max, width = 20) {
  if (max <= 0) return '';
  const filled = Math.round((value / max) * width);
  return repeat('‚ñâ', filled);
}

const now = new Date();
const dateStr = now.toLocaleString('en-US', { year:'numeric', month:'short', day:'2-digit' });

const inp = $input.all();
const books = (inp?.[0]?.json?.output && Array.isArray(inp[0].json.output))
  ? inp[0].json.output
  : Array.isArray(inp?.[0]?.json) ? inp[0].json : [];

const clean = books.map(b => ({
  title: safe(b.title, 'N/A'),
  author: safe(b.author, 'Unknown'),
  rank: Number.isFinite(b.rank) ? b.rank : null,
  category: safe(b.category, 'N/A'),
  sub_category: safe(b.sub_category, 'N/A'),
  rating: Number.isFinite(b.rating) ? b.rating : 0,
  ratings_count: Number.isFinite(b.ratings_count) ? b.ratings_count : 0,
  price_amount: Number.isFinite(b?.price?.amount) ? b.price.amount : 0,
  price_currency: safe(b?.price?.currency, 'USD'),
  price_format: safe(b?.price?.format, 'Unknown'),
}));

// --- Core metrics ---
const totalBooks = clean.length;
const currency = clean.find(b => b.price_currency)?.price_currency || 'USD';
const prices = clean.map(b => b.price_amount).filter(n => Number.isFinite(n) && n >= 0);
const totalSpend = sum(prices);
const avgPrice = mean(prices);
const medPrice = median(prices);
const minPrice = prices.length ? Math.min(...prices) : 0;
const maxPrice = prices.length ? Math.max(...prices) : 0;

const rated = clean.filter(b => b.rating > 0);
const avgRating = rated.length ? mean(rated.map(b => b.rating)) : 0;
const ratingCoverage = rated.length;
const unratedCount = totalBooks - ratingCoverage;

// category/subcategory counts
const catCount = {};
const subCatCount = {};
for (const b of clean) {
  catCount[b.category] = (catCount[b.category] || 0) + 1;
  subCatCount[b.sub_category] = (subCatCount[b.sub_category] || 0) + 1;
}

// formats
const formatCount = {};
for (const b of clean) {
  formatCount[b.price_format] = (formatCount[b.price_format] || 0) + 1;
}

// top by popularity (ratings_count) and by rating (>=50 ratings)
const topByRatingsCount = [...clean].sort((a,b) => b.ratings_count - a.ratings_count).slice(0,5);
const topByRating = [...clean]
  .filter(b => b.ratings_count >= 50 && b.rating > 0)
  .sort((a,b) => b.rating - a.rating)
  .slice(0,5);

// price histogram (simple buckets)
const bucketSize = 5; // $5 buckets
const buckets = {};
for (const p of prices) {
  const b = Math.floor(p / bucketSize) * bucketSize;
  const label = `${fmtMoney(b, currency)}‚Äì${fmtMoney(b + bucketSize - 0.01, currency)}`;
  buckets[label] = (buckets[label] || 0) + 1;
}
const bucketEntries = Object.entries(buckets).sort((a,b) => {
  // sort by numeric lower bound
  const n = s => Number(s[0].replace(/[^0-9.]/g, ''));
  return n(a) - n(b);
});
const maxBucket = bucketEntries.length ? Math.max(...bucketEntries.map(([_, v]) => v)) : 0;

// category table (top 5)
const topCats = Object.entries(catCount).sort((a,b) => b[1]-a[1]).slice(0,5);
const topSubCats = Object.entries(subCatCount).sort((a,b) => b[1]-a[1]).slice(0,5);
const topFormats = Object.entries(formatCount).sort((a,b) => b[1]-a[1]).slice(0,5);

// build tables
function makeKpiRow(label, value) {
  return `| ${label} | ${value} |`;
}

function tableHeader(cols) {
  return `| ${cols.join(' | ')} |\n| ${cols.map(()=>'---').join(' | ')} |`;
}

function bookRow(b) {
  const title = b.title.length > 72 ? b.title.slice(0,69) + '‚Ä¶' : b.title;
  return `| ${safe(b.rank,'‚Äì')} | ${title} | ${b.author} | ${fmtMoney(b.price_amount, b.price_currency)} | ${b.rating || '‚Äì'} | ${b.ratings_count} |`;
}

const kpiTable = [
  tableHeader(['Metric','Value']),
  makeKpiRow('Total Books', totalBooks),
  makeKpiRow('Total Spend (list prices)', fmtMoney(totalSpend, currency)),
  makeKpiRow('Avg Price', fmtMoney(avgPrice, currency)),
  makeKpiRow('Median Price', fmtMoney(medPrice, currency)),
  makeKpiRow('Price Range', `${fmtMoney(minPrice, currency)} ‚Äì ${fmtMoney(maxPrice, currency)}`),
  makeKpiRow('Avg Rating (rated only)', rated.length ? avgRating.toFixed(2) : '‚Äì'),
  makeKpiRow('Rated Titles', `${ratingCoverage}/${totalBooks}`),
].join('\n');

const popularityTable = [
  tableHeader(['Rank','Title','Author','Price','Rating','#Ratings']),
  ...topByRatingsCount.map(bookRow),
].join('\n');

const qualityTable = [
  tableHeader(['Rank','Title','Author','Price','Rating','#Ratings']),
  ...topByRating.map(bookRow),
].join('\n');

function kvTable(title, entries) {
  const rows = entries.map(([k,v]) => `| ${k} | ${v} |`).join('\n');
  return `**${title}**\n\n${tableHeader(['Key','Count'])}\n${rows}`;
}

const catTable = kvTable('Top Categories', topCats);
const subCatTable = kvTable('Top Sub-Categories', topSubCats);
const fmtTable = kvTable('Formats', topFormats);

// histogram block
const histLines = bucketEntries.map(([label, count]) => {
  const bar = asBar(count, maxBucket, 24);
  return `- ${pad(label, 22)} | ${pad(count, 3)} ${bar}`;
}).join('\n');

const highlights = [];
if (topByRatingsCount[0]) {
  highlights.push(`**Most talked-about**: _${topByRatingsCount[0].title}_ (${topByRatingsCount[0].ratings_count.toLocaleString()} ratings).`);
}
if (topByRating[0]) {
  highlights.push(`**Highest rated (‚â•50 ratings)**: _${topByRating[0].title}_ (${topByRating[0].rating.toFixed(1)}‚òÖ).`);
}
if (topFormats[0]) {
  highlights.push(`**Preferred format**: ${topFormats[0][0]} (${topFormats[0][1]} titles).`);
}
if (topCats[0]) {
  highlights.push(`**Top category**: ${topCats[0][0]} (${topCats[0][1]} titles).`);
}

const recs = [];
if (unratedCount > 0) {
  recs.push(`Fill in missing ratings for ${unratedCount} titles to improve quality insights.`);
}
if (prices.some(p => p === 0)) {
  const zeroPrices = prices.filter(p => p === 0).length;
  recs.push(`Assign prices to ${zeroPrices} title(s) with \$0 to avoid skewing spend/price stats.`);
}
if (clean.some(b => !b.rank)) {
  recs.push(`Confirm ranks for all titles to refine top-N comparisons.`);
}

const markdown = `
# üìö Book Purchase Report
**Date:** ${dateStr}

> A quick, readable snapshot of your current book list with pricing, ratings, and category insights.

---

## üîé Executive KPIs
${kpiTable}

---

## ‚≠ê Most Popular (by #Ratings)
${popularityTable}

---

## üèÜ Highest Rated (‚â• 50 ratings)
${qualityTable}

---

## üß© Breakdown
${catTable}

${subCatTable}

${fmtTable}

---

## üí≤ Price Distribution (bucket = \$${bucketSize})
\`\`\`
${histLines || 'No price data available.'}
\`\`\`

---

## ‚ú® Highlights
${highlights.length ? highlights.map(x => `- ${x}`).join('\n') : '- No highlights available yet.'}

---

## ‚úÖ Suggestions
${(recs.length ? recs : ['All good!']).map(x => `- ${x}`).join('\n')}
`.trim();

// Also expose structured stats if you want to reuse downstream
const stats = {
  totalBooks,
  currency,
  price: {
    totalSpend,
    avgPrice,
    medianPrice: medPrice,
    minPrice,
    maxPrice,
  },
  ratings: {
    avgRating: rated.length ? Number(avgRating.toFixed(3)) : null,
    ratedCount: ratingCoverage,
    unratedCount,
  },
  counts: {
    byCategory: catCount,
    bySubCategory: subCatCount,
    byFormat: formatCount,
  },
};

return [
  {
    json: {
      markdown,
      stats,
    }
  }
];
