const input = $json;

// Journal to debug
console.log("=== DATA RECEIVED ===");
console.log(JSON.stringify(input, null, 2));

// stop if request empty
const quickCheck = input.body?.ticket?.summary || input.ticket?.summary || input.summary;
if (!quickCheck || quickCheck.toString().trim() === "") {
  console.log("ARRÊT : Empty request detected");
  return [{ json: { blocked: true, reason: "empty_request" } }];
}

// check: stop if no useful data
if (!input || Object.keys(input).length === 0) {
  console.log("ARRÊT : No data received");
  return [{ json: { blocked: true, reason: "no_data" } }];
}

// Extraction
const getValue = (obj, keys, defaultValue = null) => {
  for (const key of keys) {
    if (obj && obj[key] !== undefined && obj[key] !== null && obj[key] !== "") {
      return obj[key];
    }
  }
  return defaultValue;
};

// In case if data are in input.ticket
const ticketData = input.ticket || input.body?.ticket || input;

// Extract with strict validation
const projectKey = getValue(ticketData, ['projectKey', 'project'], "TES");
const summary = getValue(ticketData, ['summary', 'title', 'name']);
const type = getValue(ticketData, ['type', 'issuetype'], "Task");
const description = getValue(ticketData, ['description', 'desc']);
const priority = getValue(ticketData, ['priority', 'severity']);
const storyPoints = getValue(ticketData, ['story_points', 'storyPoints', 'points']);
const assignee = getValue(ticketData, ['assignee', 'assignedTo', 'owner']);

// critical validation, stop if  summary is empty
if (!summary || summary.trim() === "" || summary === "Ticket without title") {
  console.log("ARRÊT : Empty Summary or by default detected");
  return [{ json: { blocked: true, reason: "no_summary" } }];
}

// critical validation, stop if  description is empty
if (!description || description.trim() === "" || description === "Aucune description fournie") {
  console.log("STOP : Empty Description or by default detected");
  return [{ json: { blocked: true, reason: "no_description" } }];
}

console.log("=== Values Validated ===");
console.log(JSON.stringify({
  projectKey,
  summary,
  type,
  description,
  priority,
  storyPoints,
  assignee
}, null, 2));

//  payload only if data are ok
const payload = {
  fields: {
    project: { key: projectKey.toString().toUpperCase() },
    summary: summary.toString().trim(),
    issuetype: { name: type.toString() },
    description: {
      type: "doc",
      version: 1,
      content: [{
        type: "paragraph",
        content: [{ type: "text", text: description.toString().trim() }]
      }]
    }
  }
};


if (priority && priority !== "null" && priority.trim() !== "") {
  payload.fields.priority = { name: priority.toString().trim() };
}

// Story points : ID can change depending on Jira
if (storyPoints !== null && storyPoints !== undefined && !isNaN(parseInt(storyPoints))) {
payload.fields.customfield_10016 = parseInt(storyPoints);
}

// Assignee deleted : needs accountId in Jira Cloud, not 'name'
// if (assignee && assignee.trim() !== "") {
//   payload.fields.assignee = { name: assignee.toString().trim() };
// }

console.log("=== FINAL PAYLOAD VALIDATED ===");
console.log(JSON.stringify(payload, null, 2));

return [{ json: payload }];

return [{ json: {
  ok: true,
  jiraKey: $json.key,
  url: `https://YOURJIRAURL.atlassian.net/browse/${$json.key}`
}}];

// Block empty request
const hasValidSummary = $json.body?.ticket?.summary || $json.ticket?.summary || $json.summary;

if (!hasValidSummary || hasValidSummary.trim() === "") {
  console.log("Empty request detected - blocked");
  return [{ json: { blocked: true, reason: "empty_request" } }];
}

// allow valid data
return items;
