// n8n Code node (Node.js)
// Input expectation:
//  - Previous node (HTTP Request) returns the Zoho labels JSON you showed,
//    typically available as items[0].json (or items[0].json.body in some setups).
//  - Optionally pass a category on the incoming item, e.g. items[0].json.category = "support"
//
// Output:
//  - json.labelMap        -> { [displayNameLowerCase]: labelId }
//  - json.selectedLabelId -> labelId for provided category (if any), else null

// Try to grab the payload regardless of whether it's at json or json.body
const root = items[0]?.json?.body ?? items[0]?.json;

// Normalize to the array you posted, then to its `.data` array
const dataArray =
  Array.isArray(root)        ? root[0]?.data :
  Array.isArray(root?.data)  ? root.data     :
  null;

if (!Array.isArray(dataArray)) {
  throw new Error('Could not find labels array at input[0].json (or .json.body)[0].data');
}

// Build a case-insensitive map: displayName (lowercase) -> labelId
const labelMap = {};
for (const entry of dataArray) {
  if (!entry?.displayName || !entry?.labelId) continue;
  labelMap[entry.displayName.toLowerCase()] = entry.labelId;
}

// If caller provided a category, resolve it (case-insensitive)
const categoryRaw =
  items[0]?.json?.category ??
  items[0]?.json?.Category ??
  items[0]?.json?.displayName ?? '';

const category = String(categoryRaw).trim().toLowerCase();
const out = { labelMap };

if (category) {
  out.selectedLabelId = labelMap[category] ?? null;
}

return [{ json: out }];
