// Get the AI output
const aiOutput = $input.first().json.output || $input.first().json.text;

console.log('Raw AI Output:', aiOutput);

if (!aiOutput) {
  return {
    json: {
      success: false,
      error: "No output from AI",
      names: []
    }
  };
}

// Split the names into an array
let names = [];

try {
  // Split by lines and clean up
  names = aiOutput.toString()
    .split('\n')
    .map(line => line.trim())
    .filter(line => line.length > 0)
    .map(line => {
      // Remove numbering if present (1. 2. etc.)
      let cleaned = line.replace(/^\d+[\.\)]\s*/, '');
      return cleaned.trim();
    })
    .filter(name => name.length > 0 && name.length < 50); // Filter out overly long responses

} catch (error) {
  console.error('Error parsing names:', error);
  return {
    json: {
      success: false,
      error: "Failed to parse names",
      names: []
    }
  };
}

// Return structured response for easy integration
return {
  json: {
    success: true,
    names: names,
    count: names.length,
    generated_at: new Date().toISOString(),
    request_params: {
      gender: $('Webhook Trigger (Name Request)').item.json.body.gender,
      requested_count: $('Webhook Trigger (Name Request)').item.json.body.count,
      reference_name: $('Webhook Trigger (Name Request)').item.json.body.reference_name || null
    }
  }
};
