// Version s√©curis√©e qui g√®re les feuilles existantes
const agentOutput = $('Gemini Research Orchestrator').item.json.output;
const spreadsheetId = $('Create Research Report').item.json.spreadsheetId;

// Fonction pour parser le markdown (identique)
function parseAgentOutput(output) {
  const lines = output.split('\n').filter(line => line.trim() !== '');
  const data = {
    title: '',
    categories: [],
    jobs: []
  };

  let currentSection = '';
  let inTable = false;
  let tableHeaders = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    if (line.startsWith('Voici les donn√©es extraites')) {
      data.title = line.replace(/\*\*/g, '').replace('Voici les donn√©es extraites du site ', '').replace(/[`:`]/g, '');
    }

    if (line.startsWith('**') && line.endsWith(':**')) {
      currentSection = line.replace(/\*\*/g, '').replace(':', '');
      inTable = false;
    }

    if (currentSection === 'Cat√©gories d\'emploi' && line.startsWith('*   ')) {
      data.categories.push(line.replace('*   ', ''));
    }

    if (line.startsWith('|') && !inTable) {
      const headers = line.split('|').map(h => h.trim()).filter(h => h !== '');
      if (headers.length > 0) {
        tableHeaders = headers;
        inTable = true;
        i++;
        continue;
      }
    }

    if (inTable && line.startsWith('|')) {
      const cells = line.split('|').map(c => c.trim()).filter(c => c !== '');
      if (cells.length === tableHeaders.length) {
        const job = {};
        tableHeaders.forEach((header, index) => {
          job[header] = cells[index] || '';
        });
        data.jobs.push(job);
      }
    }
  }

  return data;
}

// Fonction pour cr√©er ou mettre √† jour une feuille
function createOrUpdateSheet(sheetName, sheetId, properties = {}) {
  if (sheetId === 0) {
    // Pour la feuille par d√©faut (ID 0), on la met √† jour
    return {
      updateSheetProperties: {
        properties: {
          sheetId: sheetId,
          title: sheetName,
          gridProperties: {
            rowCount: 1000,
            columnCount: 26
          },
          ...properties
        },
        fields: 'title,tabColor,gridProperties'
      }
    };
  } else {
    // Pour les nouvelles feuilles, on les cr√©e
    return {
      addSheet: {
        properties: {
          sheetId: sheetId,
          title: sheetName,
          gridProperties: {
            rowCount: 1000,
            columnCount: 26
          },
          ...properties
        }
      }
    };
  }
}

// Fonction pour ajouter des donn√©es dans une feuille
function addSheetData(sheetName, range, values, majorDimension = 'ROWS') {
  return {
    range: `'${sheetName}'!${range}`,
    majorDimension: majorDimension,
    values: values
  };
}

// Fonction pour formater les cellules
function formatCells(sheetId, range, format) {
  return {
    repeatCell: {
      range: {
        sheetId: sheetId,
        startRowIndex: range.startRow,
        endRowIndex: range.endRow,
        startColumnIndex: range.startCol,
        endColumnIndex: range.endCol
      },
      cell: {
        userEnteredFormat: format
      },
      fields: 'userEnteredFormat'
    }
  };
}

// Fonction pour obtenir les styles de formatage
function getTextFormat(style) {
  const formats = {
    'TITLE': {
      textFormat: {
        bold: true,
        fontSize: 16,
        foregroundColor: { red: 0.1, green: 0.1, blue: 0.1 }
      },
      horizontalAlignment: 'CENTER',
      backgroundColor: { red: 0.85, green: 0.85, blue: 0.95 }
    },
    'SECTION_HEADER': {
      textFormat: {
        bold: true,
        fontSize: 14,
        foregroundColor: { red: 0.2, green: 0.2, blue: 0.2 }
      },
      backgroundColor: { red: 0.9, green: 0.9, blue: 0.9 }
    },
    'TABLE_HEADER': {
      textFormat: {
        bold: true,
        fontSize: 11,
        foregroundColor: { red: 1, green: 1, blue: 1 }
      },
      backgroundColor: { red: 0.2, green: 0.4, blue: 0.8 },
      horizontalAlignment: 'CENTER',
      borders: {
        top: { style: 'SOLID', width: 2 },
        bottom: { style: 'SOLID', width: 2 },
        left: { style: 'SOLID', width: 1 },
        right: { style: 'SOLID', width: 1 }
      }
    },
    'TABLE_CELL': {
      textFormat: {
        fontSize: 10,
        foregroundColor: { red: 0.1, green: 0.1, blue: 0.1 }
      },
      borders: {
        top: { style: 'SOLID', width: 1 },
        bottom: { style: 'SOLID', width: 1 },
        left: { style: 'SOLID', width: 1 },
        right: { style: 'SOLID', width: 1 }
      },
      verticalAlignment: 'TOP'
    },
    'CATEGORY': {
      textFormat: {
        fontSize: 11,
        foregroundColor: { red: 0.3, green: 0.3, blue: 0.3 }
      },
      backgroundColor: { red: 0.95, green: 0.98, blue: 0.95 }
    }
  };
  return formats[style] || formats['TABLE_CELL'];
}

// Parsing des donn√©es
let parsedData;
try {
  parsedData = parseAgentOutput(agentOutput);
} catch (error) {
  throw new Error(`Erreur lors du parsing des donn√©es: ${error.message}`);
}

// Construction des requ√™tes et donn√©es
const requests = [];
const valueRanges = [];

try {
  // D'abord, effacer le contenu de la feuille par d√©faut
  requests.push({
    updateCells: {
      range: {
        sheetId: 0
      },
      fields: '*'
    }
  });

  // 1. Mise √† jour de la feuille par d√©faut (ID 0) -> "R√©sum√©"
  requests.push(createOrUpdateSheet('Resume', 0, {
    tabColor: { red: 0.2, green: 0.5, blue: 0.8 }
  }));

  // Donn√©es de la feuille R√©sum√©
  const summaryData = [
    [parsedData.title || 'Donn√©es extraites'],
    [''],
    ['R√©sum√© des donn√©es:'],
    [`Nombre de cat√©gories: ${parsedData.categories.length}`],
    [`Nombre d'emplois: ${parsedData.jobs.length}`],
    [''],
    ['Cat√©gories d\'emploi:']
  ];

  parsedData.categories.forEach(category => {
    summaryData.push([`‚Ä¢ ${category}`]);
  });

  valueRanges.push(addSheetData('Resume', 'A1', summaryData));

  // Formatage de la feuille R√©sum√©
  requests.push(formatCells(0, { startRow: 0, endRow: 1, startCol: 0, endCol: 3 }, getTextFormat('TITLE')));
  requests.push(formatCells(0, { startRow: 2, endRow: 3, startCol: 0, endCol: 3 }, getTextFormat('SECTION_HEADER')));
  requests.push(formatCells(0, { startRow: 6, endRow: 7, startCol: 0, endCol: 3 }, getTextFormat('SECTION_HEADER')));

  const categoryStartRow = 7;
  const categoryEndRow = categoryStartRow + parsedData.categories.length;
  requests.push(formatCells(0, { 
    startRow: categoryStartRow, 
    endRow: categoryEndRow, 
    startCol: 0, 
    endCol: 2 
  }, getTextFormat('CATEGORY')));

  // 2. Cr√©ation de la feuille "Cat√©gories"
  requests.push(createOrUpdateSheet('Categories', 1, {
    tabColor: { red: 0.8, green: 0.9, blue: 0.7 }
  }));

  const categoriesData = [
    ['Cat√©gories d\'emploi'],
    [''],
    ['Cat√©gorie', 'Num√©ro']
  ];

  parsedData.categories.forEach((category, index) => {
    categoriesData.push([category, index + 1]);
  });

  valueRanges.push(addSheetData('Categories', 'A1', categoriesData));

  requests.push(formatCells(1, { startRow: 0, endRow: 1, startCol: 0, endCol: 2 }, getTextFormat('TITLE')));
  requests.push(formatCells(1, { startRow: 2, endRow: 3, startCol: 0, endCol: 2 }, getTextFormat('TABLE_HEADER')));

  if (parsedData.categories.length > 0) {
    requests.push(formatCells(1, { 
      startRow: 3, 
      endRow: 3 + parsedData.categories.length, 
      startCol: 0, 
      endCol: 2 
    }, getTextFormat('TABLE_CELL')));
  }

  // 3. Cr√©ation de la feuille "Emplois"
  requests.push(createOrUpdateSheet('Emplois', 2, {
    tabColor: { red: 0.9, green: 0.7, blue: 0.8 }
  }));

  if (parsedData.jobs.length > 0) {
    const jobHeaders = Object.keys(parsedData.jobs[0]);
    const jobsData = [
      ['Liste des emplois disponibles'],
      [''],
      jobHeaders
    ];

    parsedData.jobs.forEach(job => {
      const jobRow = jobHeaders.map(header => job[header] || '');
      jobsData.push(jobRow);
    });

    valueRanges.push(addSheetData('Emplois', 'A1', jobsData));

    requests.push(formatCells(2, { startRow: 0, endRow: 1, startCol: 0, endCol: jobHeaders.length }, getTextFormat('TITLE')));
    requests.push(formatCells(2, { startRow: 2, endRow: 3, startCol: 0, endCol: jobHeaders.length }, getTextFormat('TABLE_HEADER')));

    if (parsedData.jobs.length > 0) {
      requests.push(formatCells(2, { 
        startRow: 3, 
        endRow: 3 + parsedData.jobs.length, 
        startCol: 0, 
        endCol: jobHeaders.length 
      }, getTextFormat('TABLE_CELL')));
    }
  }

  // 4. Cr√©ation de la feuille "Statistiques"
  requests.push(createOrUpdateSheet('Statistiques', 3, {
    tabColor: { red: 0.95, green: 0.8, blue: 0.6 }
  }));

  const companyCount = [...new Set(parsedData.jobs.map(job => job.Entreprise || job.Company || ''))].length;
  const roleTypes = {};

  parsedData.jobs.forEach(job => {
    const role = job.R√¥le || job.Role || '';
    const roleType = role.split(' ')[0];
    roleTypes[roleType] = (roleTypes[roleType] || 0) + 1;
  });

  const statsData = [
    ['Statistiques des donn√©es'],
    [''],
    ['M√©trique', 'Valeur'],
    ['Nombre total d\'emplois', parsedData.jobs.length],
    ['Nombre d\'entreprises uniques', companyCount],
    ['Nombre de cat√©gories', parsedData.categories.length],
    [''],
    ['Types de r√¥les les plus fr√©quents:'],
    ['Type de r√¥le', 'Occurrences']
  ];

  const sortedRoleTypes = Object.entries(roleTypes)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 10);

  sortedRoleTypes.forEach(([role, count]) => {
    statsData.push([role, count]);
  });

  valueRanges.push(addSheetData('Statistiques', 'A1', statsData));

  requests.push(formatCells(3, { startRow: 0, endRow: 1, startCol: 0, endCol: 2 }, getTextFormat('TITLE')));
  requests.push(formatCells(3, { startRow: 2, endRow: 3, startCol: 0, endCol: 2 }, getTextFormat('TABLE_HEADER')));
  requests.push(formatCells(3, { startRow: 7, endRow: 8, startCol: 0, endCol: 2 }, getTextFormat('SECTION_HEADER')));
  requests.push(formatCells(3, { startRow: 8, endRow: 9, startCol: 0, endCol: 2 }, getTextFormat('TABLE_HEADER')));

  // Auto-redimensionnement des colonnes
  for (let i = 0; i <= 3; i++) {
    requests.push({
      autoResizeDimensions: {
        dimensions: {
          sheetId: i,
          dimension: 'COLUMNS',
          startIndex: 0,
          endIndex: 10
        }
      }
    });
  }

} catch (error) {
  console.error('Erreur lors de la g√©n√©ration du contenu:', error);
  throw new Error(`Erreur lors de la g√©n√©ration du contenu: ${error.message}`);
}

// Validation finale des donn√©es
if (!requests || requests.length === 0) {
  throw new Error('Aucune requ√™te g√©n√©r√©e - v√©rifiez les donn√©es d\'entr√©e');
}

if (!valueRanges || valueRanges.length === 0) {
  throw new Error('Aucune donn√©e g√©n√©r√©e - v√©rifiez les donn√©es d\'entr√©e');
}

// Retour des donn√©es pour l'API Google Sheets
return {
  json: {
    spreadsheetId: spreadsheetId,
    requests: requests,
    valueRanges: valueRanges,
    totalRequests: requests.length,
    totalValueRanges: valueRanges.length,
    sheetsCreated: 4,
    dataProcessed: {
      title: parsedData.title,
      categoriesCount: parsedData.categories.length,
      jobsCount: parsedData.jobs.length,
      companiesCount: [...new Set(parsedData.jobs.map(job => job.Entreprise || job.Company || ''))].length
    }
  }
};
