conditions.leftValue:
={{
  (() => {
    const target = String($node["Generate Sheet Name"].json["Sheet name"] ?? "")
      .replace(/[\r\n\u2028\u2029]/g,"").replace(/\\n/g,"").trim();

    const titles = ($node["Get Order Sheets metadata"].json.sheets || [])
      .map(s => String(s.properties?.title ?? "")
        .replace(/[\r\n\u2028\u2029]/g,"").replace(/\\n/g,"").trim());

    return titles.includes(target);   // <-- boolean
  })()
}}

number.value:
={{
  (() => {
    const tz='Asia/Kolkata';
    const d = new Date($('Order created').item.json.body.updated_at);
    const y = +d.toLocaleString('en-US',{year:'numeric',  timeZone:tz});
    const m = +d.toLocaleString('en-US',{month:'2-digit', timeZone:tz});
    const day = +d.toLocaleString('en-US',{day:'2-digit', timeZone:tz});
    const serial = Date.UTC(y, m-1, day) / 86400000 + 25569;
    return Math.round(serial * 1e5) / 1e5;   // number; no leading apostrophe
  })()
}}

string.value:
={{
  (( $('Order created').item.json.body.line_items ?? $json.line_items) || [])
