  (() => {
    // take whatever you stored earlier as "Sheet name"
    const raw = String($json['Sheet name'] ?? '');

    // 1) remove real line breaks + literal "\n"
    const clean = raw
      .replace(/[\r\n\u2028\u2029]/g, '')  // real newlines
      .replace(/\\n/g, '')                 // literal backslash+n
      .trim();

    // 2) escape apostrophes for A1 and wrap in single quotes
    const quoted = `'${ clean.replace(/'/g, "''") }'`;

    // 3) return A1 range
    return `${quoted}!A1`;
  })()
}}
