parameters.jsCode:
// Format the structured AI analysis for Slack Block Kit
const data = $input.item.json;
const analysis = Array.isArray(data) ? data[0].output : (data.output || data);
const config = $('Configuration').item.json;

// Helper function to format date nicely
const formatDate = (dateStr) => {
  const date = dateStr ? new Date(dateStr) : new Date();
  return date.toLocaleDateString('en-US', { 
    weekday: 'long', 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  });
};

// Helper to truncate text to avoid Slack's block limits
const truncateText = (text, maxLength = 3000) => {
  if (text && text.length > maxLength) {
    return text.substring(0, maxLength - 3) + '...';
  }
  return text || '';
};

// Use the TLDR from the AI agent or create a fallback
let tldrSummary = "";
if (analysis.executive_summary?.tldr) {
  tldrSummary = analysis.executive_summary.tldr;
} else if (analysis.executive_summary?.developments && analysis.executive_summary.developments.length > 0) {
  const devCount = analysis.executive_summary.developments.length;
  const topics = analysis.executive_summary.developments
    .slice(0, 2)
    .map(dev => dev.title.split(' ').slice(0, 4).join(' '))
    .join(" and ");
  tldrSummary = `${devCount} key developments today: ${topics}. Focus on signal over noise.`;
} else {
  tldrSummary = "No developments met our strict relevance criteria today. This is valuable signal - not every day brings operationally relevant news.";
}

// Build the blocks array
let blocks = [
  {
    "type": "header",
    "text": {
      "type": "plain_text",
      "text": `üì∞ | ${config.brief_title || 'Intelligence Brief'}`
    }
  },
  {
    "type": "context",
    "elements": [
      {
        "text": `üìÖ ${formatDate(analysis.metadata?.date_analyzed)} | üë§ ${config.recipient_name || 'Team'}`,
        "type": "mrkdwn"
      }
    ]
  },
  {
    "type": "divider"
  },
  {
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": "üì¢ | *TL;DR*"
    }
  },
  {
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": truncateText(tldrSummary, 3000)
    }
  },
  {
    "type": "divider"
  }
];

// Add news summary if we have developments
if (analysis.executive_summary?.developments && analysis.executive_summary.developments.length > 0) {
  blocks.push({
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": "üì∞ | *KEY DEVELOPMENTS*"
    }
  });

  // Build rich text elements for each development
  let richTextElements = [];

  analysis.executive_summary.developments.forEach((dev, index) => {
    // Add title with source
    richTextElements.push({
      "type": "rich_text_section",
      "elements": [
        {
          "type": "text",
          "text": `${dev.title}`,
          "style": {
            "code": true
          }
        },
        {
          "type": "text",
          "text": ` - ${dev.source || 'Newsletter'}`,
          "style": {
            "italic": true
          }
        },
        {
          "type": "text",
          "text": "\n"
        }
      ]
    });

    // Add content in quote block 
    richTextElements.push({
      "type": "rich_text_quote",
      "elements": [
        {
          "type": "text",
          "text": "TL;DR:",
          "style": {
            "bold": true
          }
        },
        {
          "type": "text",
          "text": `\n${truncateText(dev.tldr_development, 500)}\n\n`
        },
        {
          "type": "text",
          "text": "Why it matters:",
          "style": {
            "bold": true
          }
        },
        {
          "type": "text",
          "text": `\n${truncateText(dev.operational_impact, 500)}\n\n`
        },
        {
          "type": "text",
          "text": "Implementation Reality:",
          "style": {
            "bold": true
          }
        },
        {
          "type": "text",
          "text": `\n${truncateText(dev.implementation_reality, 500)}\n\n`
        }
      ]
    });

    // Add separator between items (except after last item)
    if (index < analysis.executive_summary.developments.length - 1) {
      richTextElements.push({
        "type": "rich_text_section",
        "elements": [
          {
            "type": "text",
            "text": "\n"
          }
        ]
      });
    }
  });

  richTextElements.push({
    "type": "rich_text_section",
    "elements": []
  });

  blocks.push({
    "type": "rich_text",
    "elements": richTextElements
  });

  blocks.push({
    "type": "divider"
  });
}

// Add content questions if we have them
if (analysis.content_questions && analysis.content_questions.length > 0) {
  blocks.push({
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": "ü§î | *CONTENT INSPIRATION*"
    }
  });

  let questionElements = [];

  analysis.content_questions.forEach((q, index) => {
    questionElements.push({
      "type": "rich_text_quote",
      "elements": [
        {
          "type": "text",
          "text": `"${truncateText(q.question, 500)}"\n\n`,
          "style": {
            "bold": true
          }
        },
        {
          "type": "text",
          "text": truncateText(q.context, 500),
          "style": {
            "italic": true
          }
        }
      ]
    });

    if (q.content_pillar) {
      questionElements.push({
        "type": "rich_text_section",
        "elements": [
          {
            "type": "text",
            "text": `üìÅ Content Pillar: ${q.content_pillar}`,
            "style": {
              "italic": true
            }
          }
        ]
      });
    }

    if (index < analysis.content_questions.length - 1) {
      questionElements.push({
        "type": "rich_text_section",
        "elements": [
          {
            "type": "text",
            "text": "\n"
          }
        ]
      });
    }
  });

  questionElements.push({
    "type": "rich_text_section",
    "elements": []
  });

  blocks.push({
    "type": "rich_text",
    "elements": questionElements
  });

  blocks.push({
    "type": "divider"
  });
}

// Add hidden patterns if present
if (analysis.hidden_patterns?.pattern) {
  blocks.push({
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": "üîç | *EMERGING PATTERN*"
    }
  });

  blocks.push({
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": `_${truncateText(analysis.hidden_patterns.pattern, 3000)}_`
    }
  });

  if (analysis.hidden_patterns.relevance) {
    blocks.push({
      "type": "context",
      "elements": [
        {
          "type": "mrkdwn",
          "text": `*Relevance:* ${truncateText(analysis.hidden_patterns.relevance, 2900)}`
        }
      ]
    });
  }

  blocks.push({
    "type": "divider"
  });
}

// Add footer
blocks.push({
  "type": "context",
  "elements": [
    {
      "type": "mrkdwn",
      "text": `_${config.footer_message || 'Focus on signal, not noise. Make informed decisions.'}_`
    }
  ]
});

// Add stats if available
if (analysis.metadata) {
  let statsText = "";

  if (analysis.metadata.total_developments) {
    statsText += `üìä ${analysis.metadata.total_developments} developments analyzed`;

    if (analysis.executive_summary?.developments?.length) {
      statsText += `, ${analysis.executive_summary.developments.length} passed relevance threshold`;
    }
  }

  if (analysis.metadata.newsletter_count) {
    if (statsText) statsText += " | ";
    statsText += `üìß ${analysis.metadata.newsletter_count} newsletters processed`;
  }

  if (statsText) {
    blocks.push({
      "type": "context",
      "elements": [
        {
          "type": "mrkdwn",
          "text": `_${statsText}_`
        }
      ]
    });
  }
}

const blockKitJson = {
  "blocks": blocks
};

return {
  blocksUi: JSON.stringify(blockKitJson),
  rawBlocks: blocks,
  blockKitObject: blockKitJson,
  hasContent: (analysis.executive_summary?.developments?.length > 0) || 
              (analysis.content_questions?.length > 0) || 
              analysis.hidden_patterns?.pattern,
  developmentCount: analysis.executive_summary?.developments?.length || 0,
  questionCount: analysis.content_questions?.length || 0,
  metadata: analysis.metadata
};

