// Get the raw array of names from the AI Agent
let arr = items[0].json.output;

// If it's a stringified array, parse it
if (typeof arr === 'string') {
  arr = JSON.parse(arr);
}

// Ensure it's an array
if (!Array.isArray(arr)) {
  return [];
}

// Return one item per trend name
return arr.map(name => ({ json: { name } }));

parameters.jsCode:
let o = $json.output;
if (typeof o === 'string') {
  try { o = JSON.parse(o); } catch { o = {}; }
}
const trend = o.trend ?? '';
const canon = trend
  .normalize('NFKC')
  .toLowerCase()
  .replace(/^#+/,'')
  .replace(/\s+/g,' ')
  .trim();

return [{
  json: {
    platform: 'twitter',
    locale: 'US',
    ...o,            // trend, summary, search_url, etc.
    canon
  }
}];
