// Parse Telegram job search command
const message = $input.first().json.message;
const text = message.text;
const chatId = message.chat.id;
const userId = message.from.id;
const userName = message.from.first_name || 'User';

// Parse command: /jobs [keyword] [location]
const parts = text.split(' ');
let keyword = 'sales marketing';
let location = 'New York';

if (parts.length >= 2) {
  keyword = parts.slice(1, -1).join(' ') || 'sales marketing';
  location = parts[parts.length - 1] || 'New York';
}

// If only one parameter, treat as keyword
if (parts.length === 2) {
  keyword = parts[1];
  location = 'New York';
}

// Create search parameters
const searchParams = {
  keyword: keyword,
  location: location,
  chatId: chatId,
  userId: userId,
  userName: userName,
  timestamp: new Date().toISOString()
};

return [{
  json: {
    ...searchParams,
    originalMessage: message,
    searchQuery: `${keyword} in ${location}`
  }
}];

// Enhanced job processing with Telegram context
const items = $input.all();
const processedJobs = [];
let chatId, userName, searchQuery;

// Get Telegram context from first item
if (items.length > 0 && items[0].json.chatId) {
  chatId = items[0].json.chatId;
  userName = items[0].json.userName || 'User';
  searchQuery = items[0].json.searchQuery || 'jobs';
}

for (const item of items) {
  const source = item.json;

  // Skip items that are just telegram context
  if (source.chatId && !source.company_name && !source.jobkey && !source.jobId) {
    continue;
  }

  // Determine source platform
  let platform = 'unknown';
  if (source.company_name || source.job_title) {
    platform = 'linkedin';
  } else if (source.jobkey || source.jobtitle) {
    platform = 'indeed';
  } else if (source.jobId) {
    platform = 'monster';
  }

  // Standardize job data structure
  const standardizedJob = {
    platform: platform,
    job_id: source.job_id || source.jobkey || source.jobId || '',
    title: source.job_title || source.jobtitle || source.title || '',
    company: source.company_name || source.company || source.companyName || '',
    location: source.location || source.formattedLocation || '',
    description: source.description || source.snippet || '',
    salary: source.salary || source.salary_formatted || '',
    url: source.url || source.link || '',
    posted_date: source.date_posted || source.date || source.postDate || '',
    scraped_at: new Date().toISOString(),
    // Telegram context
    telegram_chat_id: chatId,
    telegram_user: userName,
    search_query: searchQuery,
    // Enhanced filtering
    is_relevant: checkRelevance(source.job_title || source.title || '', source.description || source.snippet || ''),
    experience_level: extractExperience(source.description || source.snippet || ''),
    remote_option: checkRemote(source.description || source.snippet || ''),
    salary_range: extractSalary(source.salary || source.salary_formatted || '')
  };

  processedJobs.push({ json: standardizedJob });
}

// Filter and deduplicate
const filteredJobs = processedJobs.filter(job => job.json.is_relevant);
const uniqueJobs = [];
const seen = new Set();

for (const job of filteredJobs) {
  const key = `${job.json.title}-${job.json.company}`.toLowerCase().replace(/[^a-z0-9]/g, '');
  if (!seen.has(key) && uniqueJobs.length < 10) { // Limit to 10 jobs for Telegram
    seen.add(key);
    uniqueJobs.push(job);
  }
}

// Add summary statistics
if (uniqueJobs.length > 0 && chatId) {
  const summary = {
    json: {
      telegram_chat_id: chatId,
      telegram_user: userName,
      search_query: searchQuery,
      total_jobs_found: uniqueJobs.length,
      platforms_searched: [...new Set(uniqueJobs.map(job => job.json.platform))],
      remote_jobs: uniqueJobs.filter(job => job.json.remote_option).length,
      search_timestamp: new Date().toISOString(),
      is_summary: true
    }
  };
  uniqueJobs.unshift(summary);
}

function checkRelevance(title, description) {
  const text = (title + ' ' + description).toLowerCase();
  const keywords = ['sales', 'marketing', 'business development', 'account manager', 'digital marketing', 
                   'marketing manager', 'sales representative', 'business analyst', 'account executive'];
  return keywords.some(keyword => text.includes(keyword));
}

function extractExperience(description) {
  const senior_keywords = ['senior', '5+ years', 'lead', 'principal', 'director'];
  const entry_keywords = ['entry level', '0-2 years', 'junior', 'associate'];
  const text = description.toLowerCase();

  if (senior_keywords.some(keyword => text.includes(keyword))) return 'senior';
  if (entry_keywords.some(keyword => text.includes(keyword))) return 'entry';
  return 'mid';
}

function checkRemote(description) {
  return description.toLowerCase().includes('remote') || 
         description.toLowerCase().includes('work from home') ||
         description.toLowerCase().includes('telecommute');
}

function extractSalary(salary) {
  if (!salary) return 'Not specified';
  // Clean and format salary information
  return salary.replace(/[^0-9$,k-]/gi, '').substring(0, 50);
}

return uniqueJobs;

